import java.util.*;

class OptimizedSolution {

    /**
     * Rotates an array to the right by k steps in-place.
     *
     * Time Complexity: O(n), where n is the length of the array.  Reversal algorithm performs three reversals, each taking O(n) time.
     * Space Complexity: O(1), as it operates in-place using only a constant amount of extra space.
     *
     * @param nums The input array of integers.
     * @param k    The number of steps to rotate the array to the right.
     */
    public static void rotate(int[] nums, int k) {
        int n = nums.length;
        k %= n; // Effective rotation amount

        // Handle edge cases such as empty array or no rotation needed
        if (n == 0 || k == 0) {
            return;
        }

        // 1. Reverse the entire array
        reverse(nums, 0, n - 1);

        // 2. Reverse the first k elements
        reverse(nums, 0, k - 1);

        // 3. Reverse the remaining n-k elements
        reverse(nums, k, n - 1);
    }

    /**
     * Reverses a portion of an array in-place.
     *
     * @param nums  The array to reverse.
     * @param start The starting index of the portion to reverse (inclusive).
     * @param end   The ending index of the portion to reverse (inclusive).
     */
    private static void reverse(int[] nums, int start, int end) {
        while (start < end) {
            int temp = nums[start];
            nums[start] = nums[end];
            nums[end] = temp;
            start++;
            end--;
        }
    }

    public static void main(String[] args) {
        // Test Case 1: Normal case
        int[] nums1 = {1, 2, 3, 4, 5, 6, 7};
        int k1 = 3;
        rotate(nums1, k1);
        System.out.println("Test Case 1: " + Arrays.toString(nums1)); // Expected: [5, 6, 7, 1, 2, 3, 4]

        // Test Case 2: k > n
        int[] nums2 = {-1, -100, 3, 99};
        int k2 = 2;
        rotate(nums2, k2);
        System.out.println("Test Case 2: " + Arrays.toString(nums2)); // Expected: [3, 99, -1, -100]

        // Test Case 3: k = n
        int[] nums3 = {1, 2, 3, 4, 5};
        int k3 = 5;
        rotate(nums3, k3);
        System.out.println("Test Case 3: " + Arrays.toString(nums3)); // Expected: [1, 2, 3, 4, 5]

        // Test Case 4: k = 0
        int[] nums4 = {1, 2, 3, 4, 5};
        int k4 = 0;
        rotate(nums4, k4);
        System.out.println("Test Case 4: " + Arrays.toString(nums4)); // Expected: [1, 2, 3, 4, 5]

        // Test Case 5: Single element array
        int[] nums5 = {7};
        int k5 = 2;
        rotate(nums5, k5);
        System.out.println("Test Case 5: " + Arrays.toString(nums5)); // Expected: [7]

        // Test Case 6: Negative numbers
        int[] nums6 = {-1, -100, 3, 99};
        int k6 = 5;
        rotate(nums6, k6);
        System.out.println("Test Case 6: " + Arrays.toString(nums6)); // Expected: [99, -1, -100, 3]

        // Test Case 7: Large k
        int[] nums7 = {1, 2, 3};
        int k7 = 200000;
        rotate(nums7, k7);
        System.out.println("Test Case 7: " + Arrays.toString(nums7)); // Expected: [3, 1, 2]

        // Test Case 8: All same elements
        int[] nums8 = {5, 5, 5, 5, 5};
        int k8 = 2;
        rotate(nums8, k8);
        System.out.println("Test Case 8: " + Arrays.toString(nums8)); // Expected: [5, 5, 5, 5, 5]
    }
}
