import java.util.Arrays;

public class CoinChange {

    /**
     * Calculates the fewest number of coins needed to make up the given amount.
     *
     * @param coins  An array of coin denominations.
     * @param amount The target amount.
     * @return The fewest number of coins needed, or -1 if the amount cannot be made up.
     *
     * Time Complexity: O(amount * n), where n is the number of coin denominations.
     * Space Complexity: O(amount)
     */
    public int coinChange(int[] coins, int amount) {
        // dp[i] will store the minimum number of coins needed to make up amount i
        int[] dp = new int[amount + 1];
        Arrays.fill(dp, amount + 1); // Initialize with a value greater than the maximum possible result

        dp[0] = 0; // Base case: 0 coins are needed to make up amount 0

        // Iterate through all amounts from 1 to the target amount
        for (int i = 1; i <= amount; i++) {
            // Iterate through each coin denomination
            for (int coin : coins) {
                // If the current coin denomination is less than or equal to the current amount
                if (coin <= i) {
                    // Update dp[i] with the minimum of its current value and the number of coins needed
                    // to make up the remaining amount (i - coin) plus 1 (for the current coin)
                    dp[i] = Math.min(dp[i], dp[i - coin] + 1);
                }
            }
        }

        // If dp[amount] is still greater than amount, it means the amount could not be made up
        return dp[amount] > amount ? -1 : dp[amount];
    }

    public static void main(String[] args) {
        CoinChange coinChange = new CoinChange();

        // Test case 1
        int[] coins1 = {1, 2, 5};
        int amount1 = 11;
        System.out.println("Coins: " + Arrays.toString(coins1) + ", Amount: " + amount1 + ", Result: " + coinChange.coinChange(coins1, amount1)); // Expected output: 3

        // Test case 2
        int[] coins2 = {2};
        int amount2 = 3;
        System.out.println("Coins: " + Arrays.toString(coins2) + ", Amount: " + amount2 + ", Result: " + coinChange.coinChange(coins2, amount2)); // Expected output: -1

        // Test case 3
        int[] coins3 = {1};
        int amount3 = 0;
        System.out.println("Coins: " + Arrays.toString(coins3) + ", Amount: " + amount3 + ", Result: " + coinChange.coinChange(coins3, amount3)); // Expected output: 0

        // Test case 4
        int[] coins4 = {1};
        int amount4 = 1;
        System.out.println("Coins: " + Arrays.toString(coins4) + ", Amount: " + amount4 + ", Result: " + coinChange.coinChange(coins4, amount4)); // Expected output: 1

        // Test case 5
        int[] coins5 = {1};
        int amount5 = 2;
        System.out.println("Coins: " + Arrays.toString(coins5) + ", Amount: " + amount5 + ", Result: " + coinChange.coinChange(coins5, amount5)); // Expected output: 2

        // Test case 6 (Edge case: large amount)
        int[] coins6 = {1, 5, 10, 25};
        int amount6 = 100;
        System.out.println("Coins: " + Arrays.toString(coins6) + ", Amount: " + amount6 + ", Result: " + coinChange.coinChange(coins6, amount6)); // Expected output: 4

        // Test case 7 (Edge case: amount is equal to one of the coin denominations)
        int[] coins7 = {1, 2, 5};
        int amount7 = 5;
        System.out.println("Coins: " + Arrays.toString(coins7) + ", Amount: " + amount7 + ", Result: " + coinChange.coinChange(coins7, amount7)); // Expected output: 1

         // Test case 8 (Edge case: no possible coin)
        int[] coins8 = {5};
        int amount8 = 1;
        System.out.println("Coins: " + Arrays.toString(coins8) + ", Amount: " + amount8 + ", Result: " + coinChange.coinChange(coins8, amount8)); // Expected output: -1

        // Test case 9 (Edge case: Only a large value coin)
        int[] coins9 = {186,419,83,408};
        int amount9 = 6249;
        System.out.println("Coins: " + Arrays.toString(coins9) + ", Amount: " + amount9 + ", Result: " + coinChange.coinChange(coins9, amount9)); // Expected output: 20

    }
}
