import java.util.HashMap;
import java.util.Map;

public class LongestSubstringWithoutRepeatingCharacters {

    /**
     * Finds the length of the longest substring without repeating characters.
     *
     * @param s The input string.
     * @return The length of the longest substring without repeating characters.
     *
     * Time Complexity: O(n), where n is the length of the string s.
     * Space Complexity: O(min(m, n)), where n is the length of the string s,
     *                    and m is the size of the character set (e.g., 26 for lowercase English letters,
     *                    128 for ASCII, or 256 for extended ASCII).
     */
    public static int lengthOfLongestSubstring(String s) {
        if (s == null || s.length() == 0) {
            return 0;
        }

        int n = s.length();
        int maxLength = 0;
        int start = 0;
        Map<Character, Integer> charIndexMap = new HashMap<>(); // Stores the last seen index of each character

        for (int end = 0; end < n; end++) {
            char currentChar = s.charAt(end);

            if (charIndexMap.containsKey(currentChar)) {
                // If the character is already in the substring, move the start pointer
                // to the right of the previous occurrence of the character.
                start = Math.max(start, charIndexMap.get(currentChar) + 1);
            }

            // Update the last seen index of the current character
            charIndexMap.put(currentChar, end);

            // Update the maximum length
            maxLength = Math.max(maxLength, end - start + 1);
        }

        return maxLength;
    }

    public static void main(String[] args) {
        // Test cases
        String s1 = "abcabcbb";
        System.out.println("Input: " + s1 + ", Output: " + lengthOfLongestSubstring(s1)); // Expected: 3

        String s2 = "bbbbb";
        System.out.println("Input: " + s2 + ", Output: " + lengthOfLongestSubstring(s2)); // Expected: 1

        String s3 = "pwwkew";
        System.out.println("Input: " + s3 + ", Output: " + lengthOfLongestSubstring(s3)); // Expected: 3

        String s4 = "";
        System.out.println("Input: " + s4 + ", Output: " + lengthOfLongestSubstring(s4)); // Expected: 0

        String s5 = " ";
        System.out.println("Input: " + s5 + ", Output: " + lengthOfLongestSubstring(s5)); // Expected: 1

        String s6 = "au";
        System.out.println("Input: " + s6 + ", Output: " + lengthOfLongestSubstring(s6)); // Expected: 2

        String s7 = "dvdf";
        System.out.println("Input: " + s7 + ", Output: " + lengthOfLongestSubstring(s7)); // Expected: 3

        String s8 = "tmmzuxt";
        System.out.println("Input: " + s8 + ", Output: " + lengthOfLongestSubstring(s8)); // Expected: 5

        String s9 = "abc";
        System.out.println("Input: " + s9 + ", Output: " + lengthOfLongestSubstring(s9)); // Expected: 3
    }
}
```

