import java.util.PriorityQueue;
import java.util.Collections;

public class MedianFinder {

    private PriorityQueue<Integer> maxHeap; // Stores the smaller half of the numbers
    private PriorityQueue<Integer> minHeap; // Stores the larger half of the numbers

    /**
     * Initializes the MedianFinder object.
     */
    public MedianFinder() {
        maxHeap = new PriorityQueue<>(Collections.reverseOrder()); // Max heap for smaller half
        minHeap = new PriorityQueue<>(); // Min heap for larger half
    }

    /**
     * Inserts the integer num into the data structure.
     *
     * @param num The integer to insert.
     *
     * Time Complexity: O(log n), where n is the number of elements added so far, due to heap operations.
     * Space Complexity: O(1)
     */
    public void addNum(int num) {
        maxHeap.offer(num); // Add to maxHeap first
        minHeap.offer(maxHeap.poll()); // Balance by moving largest from maxHeap to minHeap

        // Maintain the size property: maxHeap.size() >= minHeap.size()
        if (maxHeap.size() < minHeap.size()) {
            maxHeap.offer(minHeap.poll());
        }
    }

    /**
     * Returns the median of all elements so far.
     *
     * @return The median of the elements.
     *
     * Time Complexity: O(1)
     * Space Complexity: O(1)
     */
    public double findMedian() {
        if (maxHeap.size() == minHeap.size()) {
            return (maxHeap.peek() + minHeap.peek()) / 2.0;
        } else {
            return maxHeap.peek();
        }
    }

    public static void main(String[] args) {
        // Test Case 1
        MedianFinder medianFinder1 = new MedianFinder();
        medianFinder1.addNum(1);
        medianFinder1.addNum(2);
        System.out.println(medianFinder1.findMedian()); // Output: 1.5
        medianFinder1.addNum(3);
        System.out.println(medianFinder1.findMedian()); // Output: 2.0

        // Test Case 2
        MedianFinder medianFinder2 = new MedianFinder();
        medianFinder2.addNum(5);
        System.out.println(medianFinder2.findMedian()); // Output: 5.0
        medianFinder2.addNum(10);
        System.out.println(medianFinder2.findMedian()); // Output: 7.5

        // Test Case 3 - Empty MedianFinder
        MedianFinder medianFinder3 = new MedianFinder();
        medianFinder3.addNum(1);
        System.out.println(medianFinder3.findMedian()); // Output: 1.0
        medianFinder3.addNum(2);
        System.out.println(medianFinder3.findMedian()); // Output: 1.5
        medianFinder3.addNum(3);
        System.out.println(medianFinder3.findMedian()); // Output: 2.0

        // Test Case 4 - Even number of elements
        MedianFinder medianFinder4 = new MedianFinder();
        medianFinder4.addNum(1);
        medianFinder4.addNum(2);
        medianFinder4.addNum(3);
        medianFinder4.addNum(4);
        System.out.println(medianFinder4.findMedian()); // Output: 2.5

        // Test Case 5 - Negative numbers
        MedianFinder medianFinder5 = new MedianFinder();
        medianFinder5.addNum(-1);
        medianFinder5.addNum(-2);
        System.out.println(medianFinder5.findMedian()); // Output: -1.5

        // Test Case 6 - Large range of numbers
        MedianFinder medianFinder6 = new MedianFinder();
        medianFinder6.addNum(100000);
        medianFinder6.addNum(-100000);
        System.out.println(medianFinder6.findMedian()); // Output: 0.0

        // Test Case 7 - Duplicate numbers
        MedianFinder medianFinder7 = new MedianFinder();
        medianFinder7.addNum(1);
        medianFinder7.addNum(1);
        System.out.println(medianFinder7.findMedian());
        medianFinder7.addNum(1);
        System.out.println(medianFinder7.findMedian());
        medianFinder7.addNum(2);
        System.out.println(medianFinder7.findMedian());

        // Test Case 8: Sequence of operations
        MedianFinder medianFinder8 = new MedianFinder();
        System.out.println("Operation: addNum(1)");
        medianFinder8.addNum(1);
        System.out.println("Operation: addNum(2)");
        medianFinder8.addNum(2);
        System.out.println("Operation: findMedian()");
        System.out.println("Output: " + medianFinder8.findMedian());
        System.out.println("Operation: addNum(3)");
        medianFinder8.addNum(3);
        System.out.println("Operation: findMedian()");
        System.out.println("Output: " + medianFinder8.findMedian());
    }
}

