import java.util.Arrays;

public class CountSmallerRight {

    /**
     * Counts the number of smaller elements to the right of each element in the input array.
     *
     * @param input The input array of integers.
     * @return An array where each element `smallCount[i]` represents the number of elements
     *         to the right of `input[i]` that are smaller than `input[i]`.
     *
     * Time Complexity: O(n log n), where n is the length of the input array, due to the merge sort process.
     * Space Complexity: O(n), primarily due to the auxiliary array used in merge sort.
     */
    public int[] countSmaller(int[] input) {
        int n = input.length;
        int[] smallCount = new int[n];
        int[] indices = new int[n]; // Store original indices
        for (int i = 0; i < n; i++) {
            indices[i] = i;
        }

        mergeSortAndCount(input, indices, 0, n - 1, smallCount);
        return smallCount;
    }

    /**
     * Recursive merge sort function that also updates the `smallCount` array.
     *
     * @param input The input array.
     * @param indices The array of original indices.
     * @param low The starting index of the subarray.
     * @param high The ending index of the subarray.
     * @param smallCount The array to store the counts.
     */
    private void mergeSortAndCount(int[] input, int[] indices, int low, int high, int[] smallCount) {
        if (low >= high) {
            return;
        }

        int mid = low + (high - low) / 2;
        mergeSortAndCount(input, indices, low, mid, smallCount);
        mergeSortAndCount(input, indices, mid + 1, high, smallCount);
        merge(input, indices, low, mid, high, smallCount);
    }

    /**
     * Merges two sorted subarrays and updates the `smallCount` array.
     *
     * @param input The input array.
     * @param indices The array of original indices.
     * @param low The starting index of the first subarray.
     * @param mid The ending index of the first subarray.
     * @param high The ending index of the second subarray.
     * @param smallCount The array to store the counts.
     */
    private void merge(int[] input, int[] indices, int low, int mid, int high, int[] smallCount) {
        int[] mergedIndices = new int[high - low + 1];
        int i = low, j = mid + 1, k = 0;
        int rightSmallerCount = 0; // Count of smaller elements from the right subarray

        while (i <= mid && j <= high) {
            if (input[indices[i]] > input[indices[j]]) {
                mergedIndices[k++] = indices[j];
                rightSmallerCount++;
                j++;
            } else {
                smallCount[indices[i]] += rightSmallerCount; // Accumulate the count
                mergedIndices[k++] = indices[i];
                i++;
            }
        }

        while (i <= mid) {
            smallCount[indices[i]] += rightSmallerCount; // Accumulate remaining counts
            mergedIndices[k++] = indices[i];
            i++;
        }

        while (j <= high) {
            mergedIndices[k++] = indices[j];
            j++;
        }

        // Copy the merged indices back to the original indices array
        System.arraycopy(mergedIndices, 0, indices, low, mergedIndices.length);
    }

    public static void main(String[] args) {
        CountSmallerRight counter = new CountSmallerRight();

        // Test Case 1: Ascending order
        int[] input1 = {1, 2, 3, 4};
        int[] output1 = counter.countSmaller(input1);
        System.out.println("Input: " + Arrays.toString(input1));
        System.out.println("Output: " + Arrays.toString(output1)); // Expected: [0, 0, 0, 0]

        // Test Case 2: Descending order
        int[] input2 = {4, 3, 2, 1};
        int[] output2 = counter.countSmaller(input2);
        System.out.println("Input: " + Arrays.toString(input2));
        System.out.println("Output: " + Arrays.toString(output2)); // Expected: [3, 2, 1, 0]

        // Test Case 3: Mixed order
        int[] input3 = {9, 2, 4, 5, 1};
        int[] output3 = counter.countSmaller(input3);
        System.out.println("Input: " + Arrays.toString(input3));
        System.out.println("Output: " + Arrays.toString(output3)); // Expected: [4, 1, 1, 1, 0]

        // Test Case 4: Empty array
        int[] input4 = {};
        int[] output4 = counter.countSmaller(input4);
        System.out.println("Input: " + Arrays.toString(input4));
        System.out.println("Output: " + Arrays.toString(output4)); // Expected: []

        // Test Case 5: Array with duplicate elements
        int[] input5 = {5, 2, 6, 1, 6};
        int[] output5 = counter.countSmaller(input5);
        System.out.println("Input: " + Arrays.toString(input5));
        System.out.println("Output: " + Arrays.toString(output5)); // Expected: [2, 1, 1, 0, 0]

        // Test Case 6: Array with all same elements
        int[] input6 = {5, 5, 5, 5};
        int[] output6 = counter.countSmaller(input6);
        System.out.println("Input: " + Arrays.toString(input6));
        System.out.println("Output: " + Arrays.toString(output6)); // Expected: [0, 0, 0, 0]

        // Test Case 7: Large array
        int[] input7 = new int[1000];
        for (int i = 0; i < 1000; i++) {
            input7[i] = (int) (Math.random() * 1000);
        }
        int[] output7 = counter.countSmaller(input7);
        System.out.println("Input (first 10 elements): " + Arrays.toString(Arrays.copyOfRange(input7, 0, 10)));
        System.out.println("Output (first 10 elements): " + Arrays.toString(Arrays.copyOfRange(output7, 0, 10)));
    }
}
