import java.util.Arrays;
import java.util.Random;

public class ArraySorter {

    /**
     * Sorts an array of integers in ascending order using the optimized QuickSort algorithm.
     *
     * @param arr The array to be sorted.
     */
    public void quickSort(int[] arr) {
        if (arr == null || arr.length <= 1) {
            return; // Already sorted or empty array
        }
        quickSortHelper(arr, 0, arr.length - 1);
    }

    /**
     * Recursive helper method for QuickSort.
     *
     * @param arr   The array to be sorted.
     * @param low   The starting index of the subarray.
     * @param high  The ending index of the subarray.
     */
    private void quickSortHelper(int[] arr, int low, int high) {
        if (low < high) {
            // Partition the array and get the pivot index
            int pivotIndex = partition(arr, low, high);

            // Recursively sort the subarrays before and after the pivot
            quickSortHelper(arr, low, pivotIndex - 1);
            quickSortHelper(arr, pivotIndex + 1, high);
        }
    }

    /**
     * Partitions the array around a pivot element.  Uses the last element as the pivot
     * and places it in its correct sorted position. Elements smaller than the pivot
     * are placed before it, and elements greater than the pivot are placed after it.
     *
     * @param arr   The array to be partitioned.
     * @param low   The starting index of the subarray.
     * @param high  The ending index of the subarray.
     * @return The index of the pivot element after partitioning.
     */
    private int partition(int[] arr, int low, int high) {
        // Choose the last element as the pivot
        int pivot = arr[high];

        // Index of smaller element
        int i = (low - 1);

        for (int j = low; j <= high - 1; j++) {
            // If current element is smaller than or equal to pivot
            if (arr[j] <= pivot) {
                i++; // increment index of smaller element
                swap(arr, i, j);
            }
        }
        swap(arr, i + 1, high);
        return (i + 1);
    }


    /**
     * Swaps two elements in an array.
     *
     * @param arr The array in which to swap elements.
     * @param i   The index of the first element.
     * @param j   The index of the second element.
     */
    private void swap(int[] arr, int i, int j) {
        int temp = arr[i];
        arr[i] = arr[j];
        arr[j] = temp;
    }

    /**
     * Main method for testing the ArraySorter class.
     *
     * @param args Command line arguments (not used).
     */
