import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

/**
 * Solution for finding the most frequent subtree sum in a binary tree.
 */
public class MostFrequentSubtreeSum {

    // TreeNode class definition (required for the problem, included for completeness)
    public static class TreeNode {
        int val;
        TreeNode left;
        TreeNode right;

        TreeNode() {
        }

        TreeNode(int val) {
            this.val = val;
        }

        TreeNode(int val, TreeNode left, TreeNode right) {
            this.val = val;
            this.left = left;
            this.right = right;
        }
    }

    /**
     * Finds the most frequent subtree sum in a binary tree.
     *
     * @param root The root of the binary tree.
     * @return A list of the most frequent subtree sums.
     *
     * Time Complexity: O(N), where N is the number of nodes in the tree. Each node is visited once.
     * Space Complexity: O(N) in the worst case, due to the HashMap storing subtree sums and the recursion stack.
     */
    public int[] findFrequentTreeSum(TreeNode root) {
        Map<Integer, Integer> sumFrequencies = new HashMap<>();
        subtreeSum(root, sumFrequencies);

        int maxFrequency = 0;
        for (int frequency : sumFrequencies.values()) {
            maxFrequency = Math.max(maxFrequency, frequency);
        }

        List<Integer> frequentSums = new ArrayList<>();
        for (Map.Entry<Integer, Integer> entry : sumFrequencies.entrySet()) {
            if (entry.getValue() == maxFrequency) {
                frequentSums.add(entry.getKey());
            }
        }

        // Convert List<Integer> to int[] for the return type.
        int[] result = new int[frequentSums.size()];
        for (int i = 0; i < frequentSums.size(); i++) {
            result[i] = frequentSums.get(i);
        }
        return result;
    }

    /**
     * Helper function to calculate the subtree sum recursively and update the frequencies.
     *
     * @param node           The current node.
     * @param sumFrequencies A map to store the frequency of each subtree sum.
     * @return The subtree sum rooted at the current node.
     */
    private int subtreeSum(TreeNode node, Map<Integer, Integer> sumFrequencies) {
        if (node == null) {
            return 0;
        }

        int leftSum = subtreeSum(node.left, sumFrequencies);
        int rightSum = subtreeSum(node.right, sumFrequencies);
        int currentSum = node.val + leftSum + rightSum;

        sumFrequencies.put(currentSum, sumFrequencies.getOrDefault(currentSum, 0) + 1);
        return currentSum;
    }


