import java.util.*;

public class FriendSuggestion {

    /**
     * Suggests a friend for a given user based on mutual connections.
     *
     * @param user        The target user.
     * @param friendships A list of friendship pairs.
     * @return A suggested friend with the most mutual connections, or null if no
     *         suggestion exists.
     *
     *         Time Complexity: O(E + V^2) where E is the number of edges (friendships) and V is the number of vertices (users)
     *         - Building the graph and finding direct friends: O(E)
     *         - BFS: O(E + V) (in the worst case, we visit all nodes and edges)
     *         - Calculating mutual connections: O(V) for each candidate suggestion
     *         - Finding the maximum mutual connections: O(V) (in the worst case)
     *
     *         Space Complexity: O(V + E)
     *         - Graph representation (adjacency list): O(V + E)
     *         - Direct friends set: O(V) (at most)
     *         - Visited set: O(V) (at most)
     *         - Candidate suggestions set: O(V) (at most)
     */
    public String suggestFriend(String user, List<List<String>> friendships) {
        // 1. Build the graph (adjacency list)
        Map<String, Set<String>> graph = buildGraph(friendships);

        // 2. Find the user's direct friends
        Set<String> directFriends = graph.getOrDefault(user, new HashSet<>());

        // 3. Perform BFS to find reachable users who are not direct friends
        Set<String> candidateSuggestions = findCandidateSuggestions(user, graph, directFriends);

        // 4. Find the candidate with the highest number of mutual connections
        String bestSuggestion = null;
        int maxMutualConnections = -1;

        for (String candidate : candidateSuggestions) {
            int mutualConnections = calculateMutualConnections(user, candidate, graph);
            if (mutualConnections > maxMutualConnections) {
                maxMutualConnections = mutualConnections;
                bestSuggestion = candidate;
            }
        }

        return bestSuggestion;
    }

    /**
     * Builds the graph (adjacency list) from the list of friendships.
     *
     * @param friendships A list of friendship pairs.
     * @return A map representing the graph (adjacency list).
     */
    private Map<String, Set<String>> buildGraph(List<List<String>> friendships) {
        Map<String, Set<String>> graph = new HashMap<>();
        for (List<String> friendship : friendships) {
            String user1 = friendship.get(0);
            String user2 = friendship.get(1);

            graph.computeIfAbsent(user1, k -> new HashSet<>()).add(user2);
            graph.computeIfAbsent(user2, k -> new HashSet<>()).add(user1);
        }
        return graph;
    }

    /**
     * Finds candidate suggestions (reachable users who are not direct friends) using
     * BFS.
     *
     * @param user        The target user.
     * @param graph       The graph (adjacency list).
     * @param directFriends The set of direct friends of the user.
     * @return A set of candidate suggestions.
     */
    private Set<String> findCandidateSuggestions(String user, Map<String, Set<String>> graph,
                                                  Set<String> directFriends) {
        Set<String> visited = new HashSet<>();
        Queue<String> queue = new LinkedList<>();
        Set<String> candidateSuggestions = new HashSet<>();

        visited.add(user);
        queue.offer(user);

        while (!queue.isEmpty()) {
            String current = queue.poll();
            Set<String> friends = graph.getOrDefault(current, new HashSet<>());

            for (String friend : friends) {
                if (!visited.contains(friend) && !directFriends.contains(friend) && !friend.equals(user)) {
                    candidateSuggestions.add(friend);
                }
                if (!visited.contains(friend)) {
                    visited.add(friend);
                    queue.offer(friend);
                }
            }
        }

        return candidateSuggestions;
    }

    /**
     * Calculates the number of mutual connections between two users.
     *
     * @param user1 The first user.
     * @param user2 The second user.
     * @param graph The graph (adjacency list).
     * @return The number of mutual connections.
     */
    private int calculateMutualConnections(String user1, String user2, Map<String, Set<String>> graph) {
        Set<String> user1Friends = graph.getOrDefault(user1, new HashSet<>());
        Set<String> user2Friends = graph.getOrDefault(user2, new HashSet<>());

        int mutualConnections = 0;
        for (String friend : user1Friends) {
            if (user2Friends.contains(friend)) {
                mutualConnections++;
            }
        }
        return mutualConnections;
    }

    public static void main(String[] args) {
        FriendSuggestion friendSuggestion = new FriendSuggestion();

        // Test Case 1
        String user1 = "A";
        List<List<String>> friendships1 = Arrays.asList(
                Arrays.asList("A", "B"),
                Arrays.asList("A", "C"),
                Arrays.asList("B", "D"),
                Arrays.asList("C", "D"),
                Arrays.asList("C", "E"));
        String suggestedFriend1 = friendSuggestion.suggestFriend(user1, friendships1);
        System.out.println("Suggested friend for " + user1 + ": " + suggestedFriend1); // Expected: D

        // Test Case 2
        String user2 = "X";
