/**
 * Problem: Populate each next pointer to point to its next right node in a perfect binary tree.
 */

import java.util.LinkedList;
import java.util.Queue;

/**
 * Definition for binary tree with next pointer.
 */
class TreeLinkNode {
    int val;
    TreeLinkNode left, right, next;

    TreeLinkNode(int x) {
        val = x;
    }

    @Override
    public String toString() {
        return "Node{" +
                "val=" + val +
                ", next=" + (next != null ? next.val : "null") +
                '}';
    }
}

public class PopulatingNextRightPointers {

    /**
     * Connects all nodes at each level of the perfect binary tree using constant extra space.
     *
     * Algorithm:
     * - Start from the root.
     * - For each node, connect its left child to its right child.
     * - If the current node has a next node (i.e., it's not the rightmost node at its level),
     *   connect its right child to its next node's left child.
     * - Move to the next level by starting from the leftmost node of the next level (i.e., root.left).
     * - Repeat until we reach the last level (i.e., root is null).
     *
     * Time Complexity: O(N), where N is the number of nodes in the tree. We visit each node once.
     * Space Complexity: O(1), constant extra space.
     *
     * @param root The root of the perfect binary tree.
     */
    public void connect(TreeLinkNode root) {
        if (root == null) {
            return;
        }

        TreeLinkNode currentLevelStart = root;

        while (currentLevelStart != null) {
            TreeLinkNode current = currentLevelStart;

            while (current != null) {
                if (current.left != null) {
                    current.left.next = current.right;
                }

                if (current.right != null && current.next != null) {
                    current.right.next = current.next.left;
                }

                current = current.next;
            }

            currentLevelStart = currentLevelStart.left; // Move to the next level's leftmost node
        }
    }

    /**
     * Helper method to create a sample perfect binary tree.
     *
     * @return The root of the created perfect binary tree.
     */
    private static TreeLinkNode createPerfectBinaryTree() {
        TreeLinkNode root = new TreeLinkNode(1);
        root.left = new TreeLinkNode(2);
        root.right = new TreeLinkNode(3);
        root.left.left = new TreeLinkNode(4);
        root.left.right = new TreeLinkNode(5);
        root.right.left = new TreeLinkNode(6);
        root.right.right = new TreeLinkNode(7);
        return root;
    }

    /**
     * Helper method to create another sample perfect binary tree.
     *
     * @return The root of the created perfect binary tree.
     */
    private static TreeLinkNode createPerfectBinaryTreeLevelTwo() {
        TreeLinkNode root = new TreeLinkNode(1);
        root.left = new TreeLinkNode(2);
        root.right = new TreeLinkNode(3);
        return root;
    }

    /**
     * Method to print the tree level by level for verification purposes.
     * @param root the root of the tree
     */
    public static void printLevelOrder(TreeLinkNode root) {
        if (root == null) {
            return;
        }

        Queue<TreeLinkNode> queue = new LinkedList<>();
        queue.offer(root);

        while (!queue.isEmpty()) {
            int levelSize = queue.size();
            for (int i = 0; i < levelSize; i++) {
                TreeLinkNode node = queue.poll();
                System.out.print(node + " ");

                if (node.left != null) {
                    queue.offer(node.left);
                }
                if (node.right != null) {
                    queue.offer(node.right);
                }
            }
            System.out.println(); // Move to the next level
        }
    }

    public static void main(String[] args) {
        PopulatingNextRightPointers solution = new PopulatingNextRightPointers();

        // Test Case 1: Perfect Binary Tree with 3 Levels
        TreeLinkNode root1 = createPerfectBinaryTree();
        System.out.println("Before connecting pointers (Tree 1):");
        printLevelOrder(root1);
        solution.connect(root1);
        System.out.println("\nAfter connecting pointers (Tree 1):");
        printLevelOrder(root1);

        // Test Case 2: Perfect Binary Tree with 2 Levels
        TreeLinkNode root2 = createPerfectBinaryTreeLevelTwo();
        System.out.println("\nBefore connecting pointers (Tree 2):");
        printLevelOrder(root2);
        solution.connect(root2);
        System.out.println("\nAfter connecting pointers (Tree 2):");
        printLevelOrder(root2);

        // Test Case 3: Empty Tree
        TreeLinkNode root3 = null;
        System.out.println("\nBefore connecting pointers (Tree 3 - Empty):");
        printLevelOrder(root3);
        solution.connect(root3);
        System.out.println("\nAfter connecting pointers (Tree 3 - Empty):");
        printLevelOrder(root3);

        //Test Case 4: Single Node
        TreeLinkNode root4 = new TreeLinkNode(1);
        System.out.println("\nBefore connecting pointers (Tree 4 - Single Node):");
        printLevelOrder(root4);
