import java.util.*;
import java.util.concurrent.*;

/**
 * A scalable and fault-tolerant notification service that supports various notification types
 * and devices. Users can customize their notification preferences. The service handles
 * traffic spikes and provides analytics.
 */
public class NotificationService {

    // Constants for default values and limits
    private static final int MAX_RETRIES = 3;
    private static final int RETRY_DELAY_MS = 1000; // 1 second

    // Data structures for storing user preferences, devices, and notification channels.
    private final ConcurrentHashMap<String, UserPreferences> userPreferences = new ConcurrentHashMap<>();
    private final ConcurrentHashMap<String, List<Device>> userDevices = new ConcurrentHashMap<>();
    private final List<NotificationChannel> notificationChannels = new ArrayList<>();

    // Executor service for asynchronous notification delivery.
    private final ExecutorService executorService = Executors.newFixedThreadPool(10); // Adjust pool size as needed

    /**
     * Registers a notification channel (e.g., SMS, Email, Push).
     *
     * @param channel The notification channel to register.
     */
    public void registerNotificationChannel(NotificationChannel channel) {
        notificationChannels.add(channel);
    }

    /**
     * Registers a user with the notification service.
     *
     * @param userId The ID of the user to register.
     */
    public void registerUser(String userId) {
        userPreferences.putIfAbsent(userId, new UserPreferences());
        userDevices.putIfAbsent(userId, new ArrayList<>());
    }

    /**
     * Adds a device to a user's profile.
     *
     * @param userId The ID of the user.
     * @param device The device to add.
     */
    public void addDevice(String userId, Device device) {
        if (!userDevices.containsKey(userId)) {
            throw new IllegalArgumentException("User not registered: " + userId);
        }
        userDevices.get(userId).add(device);
    }

    /**
     * Updates a user's notification preferences.
     *
     * @param userId    The ID of the user.
     * @param preferences The new notification preferences.
     */
    public void updatePreferences(String userId, UserPreferences preferences) {
        if (!userPreferences.containsKey(userId)) {
            throw new IllegalArgumentException("User not registered: " + userId);
        }
        userPreferences.put(userId, preferences);
    }

    /**
     * Sends a notification to a user.  This method determines the appropriate channels
     * based on user preferences and attempts delivery with retries.
     *
     * @param userId      The ID of the user to notify.
     * @param notification The notification to send.
     */
    public void sendNotification(String userId, Notification notification) {
        if (!userPreferences.containsKey(userId)) {
            System.err.println("User not registered: " + userId);
            return;
        }

        UserPreferences preferences = userPreferences.get(userId);

        // Check if the user wants to receive notifications of this type
        if (!preferences.isEnabled(notification.getType())) {
            System.out.println("Notification type disabled for user: " + userId + ", type: " + notification.getType());
            return;
        }

        List<Device> devices = userDevices.get(userId);
        if (devices == null || devices.isEmpty()) {
            System.out.println("No devices registered for user: " + userId);
            return;
        }

        // Iterate through devices and attempt to deliver the notification
        devices.forEach(device -> {
            executorService.submit(() -> {
                for (NotificationChannel channel : notificationChannels) {
                    if (channel.supportsDeviceType(device.getType()) && channel.supportsNotificationType(notification.getType())) {
                        // Attempt to deliver the notification with retries
                        attemptDelivery(channel, device, notification, userId);
                    }
                }
            });
        });
    }


    private void attemptDelivery(NotificationChannel channel, Device device, Notification notification, String userId) {
        int retries = 0;
        boolean success = false;

        while (retries < MAX_RETRIES && !success) {
            try {
                channel.sendNotification(device, notification);
                System.out.println("Notification sent successfully to user: " + userId +
                                   " via channel: " + channel.getClass().getSimpleName() +
                                   " on device: " + device.getType());
                success = true;
            } catch (Exception e) {
                System.err.println("Failed to send notification to user: " + userId +
                                   " via channel: " + channel.getClass().getSimpleName() +
                                   " on device: " + device.getType() +
                                   ", attempt: " + (retries + 1) +
                                   ", error: " + e.getMessage());
                retries++;
                try {
                    Thread.sleep(RETRY_DELAY_MS);
                } catch (InterruptedException ie) {
                    Thread.currentThread().interrupt(); // Restore interrupted state.
                    System.err.println("Retry delay interrupted.");
                }
            }
        }

        if (!success) {
            System.err.println("Failed to deliver notification to user: " + userId +
                               " after " + MAX_RETRIES + " retries via channel: " + channel.getClass().getSimpleName() +
                               " on device: " + device.getType());
        }
    }


    /**
     * Shuts down the executor service gracefully.
     */
    public void shutdown() {
        executorService.shutdown();
        try {
            if (!executorService.awaitTermination(60, TimeUnit.SECONDS)) {
                executorService.shutdownNow();
                if (!executorService.awaitTermination(60, TimeUnit.SECONDS)) {
                    System.err.println("Executor service did not terminate.");
                }
            }
        } catch (InterruptedException e) {
            executorService.shutdownNow();
            Thread.currentThread().interrupt();
        }
    }

    // Inner classes for representing the domain model.
    static class UserPreferences {
        private final Map<NotificationType, Boolean> enabledNotifications = new HashMap<>();

        public UserPreferences() {
            // Default: enable all notification types
            for (NotificationType type : NotificationType.values()) {
                enabledNotifications.put(type, true);
            }
        }

        public void enable(NotificationType type) {
            enabledNotifications.put(type, true);
        }

        public void disable(NotificationType type) {
            enabledNotifications.put(type, false);
        }

        public boolean isEnabled(NotificationType type) {
            return enabledNotifications.getOrDefault(type, false);
        }

        @Override
        public String toString() {
            return "UserPreferences{" +
                   "enabledNotifications=" + enabledNotifications +
                   '}';
        }
    }

    interface NotificationChannel {
        boolean supportsDeviceType(DeviceType deviceType);
        boolean supportsNotificationType(NotificationType notificationType);
        void sendNotification(Device device, Notification notification);
    }

    static class SMSChannel implements NotificationChannel {
        @Override
        public boolean supportsDeviceType(DeviceType deviceType) {
            return deviceType == DeviceType.MOBILE;
        }

        @Override
        public boolean supportsNotificationType(NotificationType notificationType) {
            return true; // SMS can handle all notification types.
