    static class EmailChannel implements NotificationChannel {
        @Override
        public boolean supportsDeviceType(DeviceType deviceType) {
            return deviceType == DeviceType.DESKTOP || deviceType == DeviceType.MOBILE;
        }

        @Override
        public boolean supportsNotificationType(NotificationType notificationType) {
            return notificationType != NotificationType.NEWS; // Email doesn't handle news notifications
        }

        @Override
        public void sendNotification(Device device, Notification notification) {
            System.out.println("Sending Email to " + device.getId() + ": " + notification.getMessage());
        }
    }

    static class PushNotificationChannel implements NotificationChannel {
        @Override
        public boolean supportsDeviceType(DeviceType deviceType) {
            return deviceType == DeviceType.MOBILE;
        }

         @Override
        public boolean supportsNotificationType(NotificationType notificationType) {
            return true; // Push Notifications can handle all types
        }


        @Override
        public void sendNotification(Device device, Notification notification) {
            System.out.println("Sending Push Notification to " + device.getId() + ": " + notification.getMessage());
        }
    }


    static class Device {
        private final String id;
        private final DeviceType type;

        public Device(String id, DeviceType type) {
            this.id = id;
            this.type = type;
        }

        public String getId() {
            return id;
        }

        public DeviceType getType() {
            return type;
        }

        @Override
        public String toString() {
            return "Device{" +
                   "id='" + id + '\'' +
                   ", type=" + type +
                   '}';
        }
    }

    enum DeviceType {
        MOBILE,
        DESKTOP
    }

    static class Notification {
        private final String message;
        private final NotificationType type;

        public Notification(String message, NotificationType type) {
            this.message = message;
            this.type = type;
        }

        public String getMessage() {
            return message;
        }

        public NotificationType getType() {
            return type;
        }

        @Override
        public String toString() {
            return "Notification{" +
                   "message='" + message + '\'' +
                   ", type=" + type +
                   '}';
        }
    }

