import java.util.*;
import java.lang.*;

class MinStepsToOne {

    /**
     * Calculates the minimum number of steps to reduce N to 1 using the given operations.
     *
     * @param n The starting positive integer.
     * @return The minimum number of steps to reach 1.
     *
     * Time Complexity: O(N) - due to the DP table of size N.
     * Space Complexity: O(N) - to store the DP table.
     */
    public static int minStepsToOne(int n) {
        if (n <= 1) {
            return 0; // Base case: already at 1 or invalid input
        }

        // dp[i] stores the minimum steps to reduce i to 1
        int[] dp = new int[n + 1];

        // Base case: dp[1] = 0
        dp[1] = 0;

        // Iterate from 2 to n
        for (int i = 2; i <= n; i++) {
            dp[i] = dp[i - 1] + 1; // Subtract 1

            if (i % 2 == 0) {
                dp[i] = Math.min(dp[i], dp[i / 2] + 1); // Divide by 2
            }

            if (i % 3 == 0) {
                dp[i] = Math.min(dp[i], dp[i / 3] + 1); // Divide by 3
            }
        }

        return dp[n];
    }

    public static void main(String[] args) {
        // Test cases
        int[] testCases = {4, 10, 1, 6, 15, 20, 30, 100, 1000, 10000};

        for (int testCase : testCases) {
            int result = minStepsToOne(testCase);
            System.out.println("N = " + testCase + ", Minimum Steps = " + result);
        }

        // Edge cases
        System.out.println("N = 0, Minimum Steps = " + minStepsToOne(0)); // Should return 0, handled by if(n <=1)

        // Additional Test Cases with large values

        System.out.println("N = 100000, Minimum Steps = " + minStepsToOne(100000));

        System.out.println("N = 5, Minimum Steps = " + minStepsToOne(5));
        System.out.println("N = 9, Minimum Steps = " + minStepsToOne(9));
        System.out.println("N = 11, Minimum Steps = " + minStepsToOne(11));
    }
}
