import java.util.HashMap;
import java.util.Map;

public class PatternMatching {

    /**
     * Checks if a given pattern matches the order of words in a given array.
     *
     * @param pattern The pattern string.
     * @param words   The array of words.
     * @return True if the pattern matches the order of words, false otherwise.
     */
    public static boolean wordPattern(String pattern, String[] words) {
        // Handle edge cases: Null or empty input
        if (pattern == null || words == null) {
            return false;
        }

        // If pattern and words have different lengths, they can't match.
        if (pattern.length() != words.length) {
            return false;
        }

        // Use a HashMap to store the mapping between characters in the pattern and words.
        Map<Character, String> charToWord = new HashMap<>();
        Map<String, Character> wordToChar = new HashMap<>();

        // Iterate through the pattern and words simultaneously.
        for (int i = 0; i < pattern.length(); i++) {
            char currentChar = pattern.charAt(i);
            String currentWord = words[i];

            // If the character is already in the map:
            if (charToWord.containsKey(currentChar)) {
                // Check if the mapped word is the same as the current word.
                if (!charToWord.get(currentChar).equals(currentWord)) {
                    return false; // Mismatch
                }
            } else {
                // If the character is not in the map, add it:
                // Check if the current word is already mapped to a different character
                if (wordToChar.containsKey(currentWord)) {
                    return false; // Mismatch
                }
                charToWord.put(currentChar, currentWord);
                wordToChar.put(currentWord, currentChar);
            }
        }

        // If the entire pattern is iterated without any mismatches, the pattern matches.
        return true;
    }

    public static void main(String[] args) {
        // Test cases to demonstrate the functionality of the wordPattern method.

        // Test Case 1: Basic example
        String pattern1 = "caca";
        String[] words1 = {"engine", "bogie", "engine", "bogie"};
        System.out.println("Test Case 1: Pattern \"" + pattern1 + "\", Words: " + java.util.Arrays.toString(words1) +
                           " - Result: " + wordPattern(pattern1, words1)); // Expected: true

        // Test Case 2: Different pattern and words
        String pattern2 = "abba";
        String[] words2 = {"dog", "cat", "cat", "dog"};
        System.out.println("Test Case 2: Pattern \"" + pattern2 + "\", Words: " + java.util.Arrays.toString(words2) +
                           " - Result: " + wordPattern(pattern2, words2)); // Expected: true

        // Test Case 3: Pattern and words do not match
        String pattern3 = "abba";
        String[] words3 = {"dog", "cat", "cat", "fish"};
        System.out.println("Test Case 3: Pattern \"" + pattern3 + "\", Words: " + java.util.Arrays.toString(words3) +
                           " - Result: " + wordPattern(pattern3, words3)); // Expected: false

        // Test Case 4: Pattern and words are of different lengths
        String pattern4 = "aaaa";
        String[] words4 = {"dog", "cat", "cat", "dog", "fish"};
        System.out.println("Test Case 4: Pattern \"" + pattern4 + "\", Words: " + java.util.Arrays.toString(words4) +
                           " - Result: " + wordPattern(pattern4, words4)); // Expected: false

        // Test Case 5: Pattern and words are single character/word
        String pattern5 = "a";
        String[] words5 = {"dog"};
        System.out.println("Test Case 5: Pattern \"" + pattern5 + "\", Words: " + java.util.Arrays.toString(words5) +
                           " - Result: " + wordPattern(pattern5, words5)); // Expected: true

        // Test Case 6: Pattern and words are repeating characters/words
        String pattern6 = "aaaa";
        String[] words6 = {"dog", "dog", "dog", "dog"};
        System.out.println("Test Case 6: Pattern \"" + pattern6 + "\", Words: " + java.util.Arrays.toString(words6) +
                           " - Result: " + wordPattern(pattern6, words6)); // Expected: true

        // Test Case 7: More complex pattern
        String pattern7 = "jqueryjquery";
        String[] words7 = {"is", "not", "is", "not", "is", "not", "is", "not"};
        System.out.println("Test Case 7: Pattern \"" + pattern7 + "\", Words: " + java.util.Arrays.toString(words7) +
                           " - Result: " + wordPattern("abab", words7)); // Fixed pattern input for comparison. Should be False

        // Test Case 8: Empty Input (Edge Case)
        String pattern8 = "";
        String[] words8 = {};
        System.out.println("Test Case 8: Pattern \"" + pattern8 + "\", Words: " + java.util.Arrays.toString(words8) +
                           " - Result: " + wordPattern(pattern8, words8)); // Expected: True

        String pattern9 = "abc";
