        System.out.println("Test Case 9: Pattern \"" + pattern9 + "\", Words: " + java.util.Arrays.toString(words9) +
                           " - Result: " + wordPattern(pattern9, words9)); // Expected: True

        String pattern10 = "abcabc";
        String[] words10 = {"dog", "cat", "fish", "dog", "cat", "fish"};
        System.out.println("Test Case 10: Pattern \"" + pattern10 + "\", Words: " + java.util.Arrays.toString(words10) +
                           " - Result: " + wordPattern(pattern10, words10)); // Expected: True
    }
