import java.util.*;

public class ShortestAlternatePaths {

    /**
     * Finds the shortest path from node 0 to all other nodes in a directed graph
     * such that the edge colors alternate along the path.
     *
     * @param n         The number of nodes in the graph.
     * @param redEdges  A 2D array representing red edges, where redEdges[i][0] is the source
     *                  and redEdges[i][1] is the destination.
     * @param blueEdges A 2D array representing blue edges, where blueEdges[i][0] is the source
     *                  and blueEdges[i][1] is the destination.
     * @return An array answer of length n, where answer[X] is the length of the
     *         shortest path from node 0 to node X with alternating edge colors
     *         (or -1 if no such path exists).
     */
    public int[] shortestAlternatingPaths(int n, int[][] redEdges, int[][] blueEdges) {
        // Build adjacency lists for red and blue edges.
        List<List<Integer>> redAdj = new ArrayList<>();
        List<List<Integer>> blueAdj = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            redAdj.add(new ArrayList<>());
            blueAdj.add(new ArrayList<>());
        }

        for (int[] edge : redEdges) {
            redAdj.get(edge[0]).add(edge[1]);
        }
        for (int[] edge : blueEdges) {
            blueAdj.get(edge[0]).add(edge[1]);
        }

        // Initialize the result array with -1.
        int[] result = new int[n];
        Arrays.fill(result, -1);
        result[0] = 0; // Distance from 0 to 0 is 0.

        // Use a queue to perform BFS. Store each node with the color of the last edge used.
        Queue<int[]> queue = new LinkedList<>(); // {node, color} where color is 0 for red, 1 for blue
        queue.offer(new int[]{0, 0}); // Start from node 0 with a red edge.
        queue.offer(new int[]{0, 1}); // Start from node 0 with a blue edge.

        // Keep track of visited nodes for each color to avoid cycles.
        boolean[][] visited = new boolean[n][2]; // visited[node][color]
        visited[0][0] = true;
        visited[0][1] = true;

        int steps = 0;
        while (!queue.isEmpty()) {
            steps++;
            int size = queue.size();
            for (int i = 0; i < size; i++) {
                int[] curr = queue.poll();
                int node = curr[0];
                int color = curr[1]; // 0 for red, 1 for blue

                List<Integer> adjList;
                int nextColor;
                if (color == 0) { // Last edge was red, so next edge must be blue.
                    adjList = blueAdj.get(node);
                    nextColor = 1;
                } else { // Last edge was blue, so next edge must be red.
                    adjList = redAdj.get(node);
                    nextColor = 0;
                }

                for (int neighbor : adjList) {
                    if (!visited[neighbor][nextColor]) {
                        visited[neighbor][nextColor] = true;
                        queue.offer(new int[]{neighbor, nextColor});
                        if (result[neighbor] == -1) {
                            result[neighbor] = steps;
                        }
                    }
                }
            }
        }

        return result;
    }

    public static void main(String[] args) {
        ShortestAlternatePaths solution = new ShortestAlternatePaths();

        // Test Case 1
        int n1 = 3;
        int[][] redEdges1 = {{0, 1}, {1, 2}};
        int[][] blueEdges1 = {};
        int[] result1 = solution.shortestAlternatingPaths(n1, redEdges1, blueEdges1);
        System.out.println("Test Case 1: " + Arrays.toString(result1)); // Expected: [0, 1, -1]

        // Test Case 2
        int n2 = 3;
        int[][] redEdges2 = {{0, 1}};
        int[][] blueEdges2 = {{2, 1}};
        int[] result2 = solution.shortestAlternatingPaths(n2, redEdges2, blueEdges2);
        System.out.println("Test Case 2: " + Arrays.toString(result2)); // Expected: [0, 1, -1]

        // Test Case 3
        int n3 = 3;
        int[][] redEdges3 = {{1, 0}};
        int[][] blueEdges3 = {{2, 1}};
        int[] result3 = solution.shortestAlternatingPaths(n3, redEdges3, blueEdges3);
        System.out.println("Test Case 3: " + Arrays.toString(result3)); // Expected: [0, -1, -1]

        // Test Case 4
        int n4 = 3;
        int[][] redEdges4 = {{0, 1}};
        int[][] blueEdges4 = {{1, 2}};
        int[] result4 = solution.shortestAlternatingPaths(n4, redEdges4, blueEdges4);
        System.out.println("Test Case 4: " + Arrays.toString(result4)); // Expected: [0, 1, 2]

        // Test Case 5
        int n5 = 3;
        int[][] redEdges5 = {{0, 1}, {0, 2}};
        int[][] blueEdges5 = {{1, 0}};
        int[] result5 = solution.shortestAlternatingPaths(n5, redEdges5, blueEdges5);
        System.out.println("Test Case 5: " + Arrays.toString(result5)); // Expected: [0, 1, 1]

        // Test Case 6 (Edge case: no edges)
        int n6 = 3;
        int[][] redEdges6 = {};
        int[][] blueEdges6 = {};
        int[] result6 = solution.shortestAlternatingPaths(n6, redEdges6, blueEdges6);
        System.out.println("Test Case 6: " + Arrays.toString(result6)); // Expected: [0, -1, -1]

        // Test Case 7 (Edge case: Self-loop)
        int n7 = 3;
        int[][] redEdges7 = {{0, 0}};
        int[][] blueEdges7 = {};
        int[] result7 = solution.shortestAlternatingPaths(n7, redEdges7, blueEdges7);
        System.out.println("Test Case 7: " + Arrays.toString(result7)); // Expected: [0, -1, -1]

        // Test Case 8 (Larger Graph)
        int n8 = 5;
        int[][] redEdges8 = {{0, 1}, {1, 2}, {2, 3}, {3, 4}};
        int[][] blueEdges8 = {{0, 2}, {2, 4}};
        int[] result8 = solution.shortestAlternatingPaths(n8, redEdges8, blueEdges8);
        System.out.println("Test Case 8: " + Arrays.toString(result8)); // Expected: [0, 1, 1, 2, 2]

         // Test Case 9 (No alternating paths for some nodes)
        int n9 = 4;
        int[][] redEdges9 = {{0, 1}, {1, 2}};
        int[][] blueEdges9 = {};
        int[] result9 = solution.shortestAlternatingPaths(n9, redEdges9, blueEdges9);
        System.out.println("Test Case 9: " + Arrays.toString(result9)); // Expected: [0, 1, 2, -1]

        // Test Case 10 (Complex Alternating Paths)
        int n10 = 5;
        int[][] redEdges10 = {{0, 1}, {0, 2}, {2, 3}};
        int[][] blueEdges10 = {{1, 2}, {2, 4}, {4,3}};
        int[] result10 = solution.shortestAlternatingPaths(n10, redEdges10, blueEdges10);
        System.out.println("Test Case 10: " + Arrays.toString(result10)); // Expected: [0, 1, 1, 2, 3]
    }

    /**
     * Time Complexity: O(n + E), where n is the number of nodes and E is the total number of edges (red + blue).
     * The BFS algorithm visits each node and edge at most once.
     *
     * Space Complexity: O(n + E), where n is the number of nodes and E is the total number of edges (red + blue).
     * - O(n) for the result array.
     * - O(n + E) for the adjacency lists.
     * - O(n) for the queue in the worst case (all nodes are in the queue).
     * - O(n) for the visited array.
     */
}
