import java.util.*;

public class AdPerformanceAnalyzer {

    /**
     * Analyzes ad performance based on purchase data, ad clicks, and user IP addresses.
     *
     * @param completedPurchaseUserIds A list of user IDs who completed a purchase.
     * @param adClicks                A list of ad click log entries.
     * @param allUserIps              A list of user IDs and their corresponding IP addresses.
     * @return A list of strings, each representing an ad's performance data in the format
     *         "purchases of clicks ad_text".
     *         Returns an empty list if any input is null or empty.
     *
     * Time Complexity: O(P + C + I + A), where P is the number of purchase IDs,
     * C is the number of ad clicks, I is the number of IP addresses, and
     * A is the number of unique ad texts.  Specifically:
     * - Creating the set of purchase IDs: O(P)
     * - Creating the IP to user ID map: O(I)
     * - Iterating through ad clicks: O(C)
     * - The inner loop in the ad click processing has a maximum of O(I) in worst case, but
     *   on average will be much faster because it only searches user ips with that IP.
     * Space Complexity: O(P + I + A), where P is the number of purchase IDs,
     * I is the number of IP addresses, and A is the number of unique ad texts.
     * - HashSet for purchase IDs: O(P)
     * - HashMap for IP to User ID: O(I)
     * - HashMap for ad performance: O(A)
     */
    public List<String> analyzeAdPerformance(List<String> completedPurchaseUserIds,
                                               List<String> adClicks,
                                               List<String> allUserIps) {

        if (completedPurchaseUserIds == null || adClicks == null || allUserIps == null ||
            completedPurchaseUserIds.isEmpty() || adClicks.isEmpty() || allUserIps.isEmpty()) {
            return new ArrayList<>(); // Handle empty input gracefully
        }

        // 1. Create a set of user IDs who made a purchase for efficient lookup.
        Set<String> purchaseUserIds = new HashSet<>(completedPurchaseUserIds);

        // 2. Create a map of IP address to User ID for quick retrieval.
        Map<String, String> ipToUserId = new HashMap<>();
        for (String userIp : allUserIps) {
            String[] parts = userIp.split(",");
            if (parts.length == 2) {
                ipToUserId.put(parts[1], parts[0]);
            }
        }

        // 3. Analyze ad clicks and count purchases per ad.
        Map<String, AdStats> adStatsMap = new HashMap<>();

        for (String adClick : adClicks) {
            String[] parts = adClick.split(",");
            if (parts.length == 3) {
                String ipAddress = parts[0];
                String adText = parts[2];

                adStatsMap.computeIfAbsent(adText, k -> new AdStats()).incrementClicks();

                String userId = ipToUserId.get(ipAddress);
                if (userId != null && purchaseUserIds.contains(userId)) {
                    adStatsMap.get(adText).incrementPurchases();
                }
            }
        }

        // 4. Format the results into a list of strings.
        List<String> results = new ArrayList<>();
        for (Map.Entry<String, AdStats> entry : adStatsMap.entrySet()) {
            AdStats stats = entry.getValue();
            results.add(stats.purchases + " of " + stats.clicks + "  " + entry.getKey());
        }

        return results;
    }

    // Helper class to store ad statistics
    private static class AdStats {
        int clicks;
        int purchases;

        public AdStats() {
            this.clicks = 0;
            this.purchases = 0;
        }

        public void incrementClicks() {
            this.clicks++;
        }

        public void incrementPurchases() {
            this.purchases++;
        }
    }

    public static void main(String[] args) {
        AdPerformanceAnalyzer analyzer = new AdPerformanceAnalyzer();

        // Test case 1: Basic test case
        List<String> completedPurchaseUserIds1 = Arrays.asList("3123122444", "234111110", "8321125440", "99911063");
