import java.util.PriorityQueue;

public class LongestHappyString {

    /**
     * Generates the longest possible happy string based on the given constraints.
     *
     * @param a The maximum number of 'a' characters allowed.
     * @param b The maximum number of 'b' characters allowed.
     * @param c The maximum number of 'c' characters allowed.
     * @return The longest happy string that can be constructed.
     *
     * Time Complexity: O(N), where N is the sum of a, b, and c.  The while loop iterates at most a+b+c times, and each operation within the loop is O(log 3) = O(1), because the PriorityQueue will always have a maximum of 3 elements. Thus the overall complexity is O(N * 1) = O(N).
     * Space Complexity: O(1), because the PriorityQueue stores at most 3 elements, and the StringBuilder's space is proportional to the length of the output string (N), but is not considered as auxiliary space.
     */
    public String longestHappyString(int a, int b, int c) {
        PriorityQueue<CharCount> pq = new PriorityQueue<>((x, y) -> y.count - x.count);

        if (a > 0) {
            pq.offer(new CharCount('a', a));
        }
        if (b > 0) {
            pq.offer(new CharCount('b', b));
        }
        if (c > 0) {
            pq.offer(new CharCount('c', c));
        }

        StringBuilder sb = new StringBuilder();
        while (!pq.isEmpty()) {
            CharCount first = pq.poll();

            if (sb.length() >= 2 && sb.charAt(sb.length() - 1) == first.ch && sb.charAt(sb.length() - 2) == first.ch) {
                if (pq.isEmpty()) {
                    break; // Cannot append without creating "aaa", "bbb", or "ccc"
                }

                CharCount second = pq.poll();
                sb.append(second.ch);
                second.count--;
                if (second.count > 0) {
                    pq.offer(second);
                }
                pq.offer(first);
            } else {
                sb.append(first.ch);
                first.count--;
                if (first.count > 0) {
                    pq.offer(first);
                }
            }
        }

        return sb.toString();
    }

    private static class CharCount {
        char ch;
        int count;

        public CharCount(char ch, int count) {
            this.ch = ch;
            this.count = count;
        }
    }

    public static void main(String[] args) {
        LongestHappyString solution = new LongestHappyString();

        // Test cases
        System.out.println("Test Case 1: a=1, b=1, c=7 -> " + solution.longestHappyString(1, 1, 7)); // Expected: "ccaccbcc"
        System.out.println("Test Case 2: a=2, b=2, c=1 -> " + solution.longestHappyString(2, 2, 1)); // Expected: "aabbc"
        System.out.println("Test Case 3: a=7, b=1, c=0 -> " + solution.longestHappyString(7, 1, 0)); // Expected: "aabaa" or "aaabaa" or "aaaabaa" or "aaaaaba" or "aaaaaab" or "aaaaaaab"
        System.out.println("Test Case 4: a=0, b=0, c=7 -> " + solution.longestHappyString(0, 0, 7)); // Expected: "cc"
        System.out.println("Test Case 5: a=0, b=7, c=0 -> " + solution.longestHappyString(0, 7, 0)); // Expected: "bb"
        System.out.println("Test Case 6: a=7, b=0, c=0 -> " + solution.longestHappyString(7, 0, 0)); // Expected: "aa"
        System.out.println("Test Case 7: a=1, b=1, c=1 -> " + solution.longestHappyString(1, 1, 1)); // Expected: "abc"
        System.out.println("Test Case 8: a=2, b=1, c=1 -> " + solution.longestHappyString(2, 1, 1)); // Expected: "aabc" or "abac"
        System.out.println("Test Case 9: a=1, b=2, c=1 -> " + solution.longestHappyString(1, 2, 1)); // Expected: "bacb" or "abcb"
        System.out.println("Test Case 10: a=1, b=1, c=2 -> " + solution.longestHappyString(1, 1, 2)); // Expected: "ccabc" or "cabc"
        System.out.println("Test Case 11: a=100, b=50, c=1 -> " + solution.longestHappyString(100, 50, 1)); // Large a
        System.out.println("Test Case 12: a=50, b=100, c=1 -> " + solution.longestHappyString(50, 100, 1)); // Large b
        System.out.println("Test Case 13: a=50, b=1, c=100 -> " + solution.longestHappyString(50, 1, 100)); // Large c
        System.out.println("Test Case 14: a=0, b=0, c=0 -> " + solution.longestHappyString(0, 0, 0)); //Empty string

