/**
 * Solution to validate if a given binary tree is a valid Binary Search Tree (BST).
 *
 * A valid BST is defined as follows:
 * 1. The left subtree of a node contains only nodes with keys less than the node's key.
 * 2. The right subtree of a node contains only nodes with keys greater than the node's key.
 * 3. Both the left and right subtrees must also be binary search trees.
 */
public class ValidateBinarySearchTree {

    /**
     * TreeNode class representing a node in the binary tree.
     */
    static class TreeNode {
        int val;
        TreeNode left;
        TreeNode right;

        TreeNode(int val) {
            this.val = val;
        }
    }

    /**
     * Main method to validate if the given binary tree is a valid BST.
     *
     * @param root The root node of the binary tree.
     * @return True if the tree is a valid BST, false otherwise.
     *
     * Time Complexity: O(N), where N is the number of nodes in the tree, as we visit each node once.
     * Space Complexity: O(H), where H is the height of the tree. In the worst case (skewed tree), H = N,
     *                   and in the best case (balanced tree), H = log N.  Due to the recursive call stack.
     */
    public boolean isValidBST(TreeNode root) {
        return isValidBSTHelper(root, null, null);
    }

    /**
     * Helper method to recursively validate the BST property.
     *
     * @param node The current node being visited.
     * @param min  The minimum allowed value for the current node (from the parent on the right). Null if there is no minimum.
     * @param max  The maximum allowed value for the current node (from the parent on the left). Null if there is no maximum.
     * @return True if the subtree rooted at the current node is a valid BST, false otherwise.
     */
    private boolean isValidBSTHelper(TreeNode node, Integer min, Integer max) {
        if (node == null) {
            return true; // Empty tree is a valid BST
        }

        // Check if the current node's value violates the BST property based on min/max constraints
        if (min != null && node.val <= min) {
            return false;
        }
        if (max != null && node.val >= max) {
            return false;
        }

        // Recursively check the left and right subtrees, updating min/max constraints accordingly
        return isValidBSTHelper(node.left, min, node.val) && isValidBSTHelper(node.right, node.val, max);
    }

    public static void main(String[] args) {
        ValidateBinarySearchTree validator = new ValidateBinarySearchTree();

        // Test Case 1: Valid BST
        TreeNode root1 = new TreeNode(2);
        root1.left = new TreeNode(1);
        root1.right = new TreeNode(3);
        System.out.println("Test Case 1: " + validator.isValidBST(root1)); // Expected: true

        // Test Case 2: Invalid BST (left subtree greater than root)
        TreeNode root2 = new TreeNode(2);
        root2.left = new TreeNode(3);
        root2.right = new TreeNode(1);
        System.out.println("Test Case 2: " + validator.isValidBST(root2)); // Expected: false

        // Test Case 3: Invalid BST (right subtree less than root)
        TreeNode root3 = new TreeNode(5);
        root3.left = new TreeNode(1);
        root3.right = new TreeNode(4);
        root3.right.left = new TreeNode(3);
        root3.right.right = new TreeNode(6);
        System.out.println("Test Case 3: " + validator.isValidBST(root3)); // Expected: false

        // Test Case 4: Single node tree (valid BST)
        TreeNode root4 = new TreeNode(1);
        System.out.println("Test Case 4: " + validator.isValidBST(root4)); // Expected: true

        // Test Case 5: Empty tree (valid BST)
        TreeNode root5 = null;
        System.out.println("Test Case 5: " + validator.isValidBST(root5)); // Expected: true

        // Test Case 6: Larger valid BST
        TreeNode root6 = new TreeNode(10);
        root6.left = new TreeNode(5);
        root6.right = new TreeNode(15);
        root6.left.left = new TreeNode(1);
        root6.left.right = new TreeNode(8);
        root6.right.left = new TreeNode(12);
        root6.right.right = new TreeNode(20);
        System.out.println("Test Case 6: " + validator.isValidBST(root6)); // Expected: true

        // Test Case 7: Larger invalid BST
        TreeNode root7 = new TreeNode(10);
        root7.left = new TreeNode(5);
        root7.right = new TreeNode(15);
        root7.left.left = new TreeNode(1);
        root7.left.right = new TreeNode(11); //Invalid
        root7.right.left = new TreeNode(12);
        root7.right.right = new TreeNode(20);
        System.out.println("Test Case 7: " + validator.isValidBST(root7)); // Expected: false

        //Test Case 8: Duplicate values in the tree.  BST shouldn't allow same value on the right
        TreeNode root8 = new TreeNode(10);
        root8.left = new TreeNode(5);
        root8.right = new TreeNode(10); //Invalid BST
        System.out.println("Test Case 8: " + validator.isValidBST(root8)); //Expected: False

        //Test Case 9: Near Integer.MAX_VALUE
        TreeNode root9 = new TreeNode(Integer.MAX_VALUE);
        root9.left = new TreeNode(Integer.MAX_VALUE - 1);
        System.out.println("Test Case 9: " + validator.isValidBST(root9)); //Expected: True

        //Test Case 10: Near Integer.MIN_VALUE
        TreeNode root10 = new TreeNode(Integer.MIN_VALUE);
        root10.right = new TreeNode(Integer.MIN_VALUE + 1);
        System.out.println("Test Case 10: " + validator.isValidBST(root10)); //Expected: True
    }
}
