import java.util.Arrays;

/**
 * A custom BigInteger class that represents arbitrarily large integers and
 * provides addition functionality.  Emphasis is placed on optimizing the
 * string addition function.
 */
public class BigInteger {

    private final int[] digits;
    private final boolean isNegative;

    /**
     * Constructor for creating a BigInteger from a string representation.
     *
     * @param number The string representation of the integer.
     * @throws IllegalArgumentException if the input string is invalid.
     */
    public BigInteger(String number) {
        if (number == null || number.isEmpty()) {
            throw new IllegalArgumentException("Input string cannot be null or empty.");
        }

        isNegative = number.startsWith("-");
        String numStr = isNegative ? number.substring(1) : number;

        if (numStr.isEmpty()) {
            throw new IllegalArgumentException("Input string cannot be just a sign.");
        }

        for (int i = 0; i < numStr.length(); i++) {
            if (!Character.isDigit(numStr.charAt(i))) {
                throw new IllegalArgumentException("Input string must contain only digits.");
            }
        }

        digits = new int[numStr.length()];
        for (int i = 0; i < numStr.length(); i++) {
            digits[i] = numStr.charAt(i) - '0';
        }
    }

    /**
     * Constructor for creating a BigInteger from an array of digits.
     *
     * @param digits     The array of digits representing the integer.
     * @param isNegative A boolean indicating whether the integer is negative.
     */
    private BigInteger(int[] digits, boolean isNegative) {
        this.digits = digits;
        this.isNegative = isNegative;
    }


    /**
     * Adds 1 to a very large number represented as a string. This function
     * is optimized for performance by operating directly on the characters
     * of the string.
     *
     * @param number The string representation of the large number.
     * @return The string representation of the number after adding 1.
     * @throws IllegalArgumentException if the input string is invalid.
     * <p>
     * Time Complexity: O(n) - where n is the length of the number string.
     * Space Complexity: O(n) - in the worst case, we need to create a new string of length n+1.
     */
    public static String addOneToString(String number) {
        if (number == null || number.isEmpty()) {
            throw new IllegalArgumentException("Input string cannot be null or empty.");
        }

        // Check if the string contains only digits
        for (int i = 0; i < number.length(); i++) {
            if (!Character.isDigit(number.charAt(i))) {
                throw new IllegalArgumentException("Input string must contain only digits.");
            }
        }

        char[] digits = number.toCharArray();
        int carry = 1;

        for (int i = digits.length - 1; i >= 0; i--) {
            int digit = digits[i] - '0';
            int sum = digit + carry;
            digits[i] = (char) ((sum % 10) + '0');
            carry = sum / 10;

            if (carry == 0) {
                break; // No more carry, we're done
            }
        }

        if (carry > 0) {
            // Prepend the carry if it's still there
            return carry + String.valueOf(digits);
        } else {
            return String.valueOf(digits);
        }
    }



    /**
     * Returns a string representation of the BigInteger.
     *
     * @return The string representation of the BigInteger.
     */
    @Override
    public String toString() {
        StringBuilder sb = new StringBuilder();
        if (isNegative) {
            sb.append("-");
        }
        for (int digit : digits) {
            sb.append(digit);
        }
        return sb.toString();
    }

    /**
     * Main method with test cases for the BigInteger class and addOneToString function.
     *
     * @param args Command line arguments (not used).
     */
    public static void main(String[] args) {
        // Test cases for addOneToString function
        System.out.println("Test Cases for addOneToString Function:");

        // Test case 1: Simple case
        String num1 = "123";
        System.out.println("Input: " + num1 + ", Result: " + addOneToString(num1));  // Expected: 124

