        System.out.println("Input: " + num5 + ", Result: " + addOneToString(num5)); // Expected: 9

        // Test case 6: Single digit 9
        String num6 = "9";
        System.out.println("Input: " + num6 + ", Result: " + addOneToString(num6)); // Expected: 10

        // Test case 7: Number ending in multiple nines
        String num7 = "199";
        System.out.println("Input: " + num7 + ", Result: " + addOneToString(num7));  // Expected: 200

        // Test case 8: zero number
        String num8 = "0";
        System.out.println("Input: " + num8 + ", Result: " + addOneToString(num8));  // Expected: 1

        // Test case 9: a long number that ends with nines
        String num9 = "1234567890999";
        System.out.println("Input: " + num9 + ", Result: " + addOneToString(num9));  // Expected: 1234567891000

        // Test cases for BigInteger class

