import java.time.Duration;
import java.time.LocalDateTime;
import java.util.*;
import java.util.concurrent.locks.Lock;
import java.util.concurrent.locks.ReentrantLock;

// Define VehicleType enum
enum VehicleType {
    MOTORCYCLE,
    CAR,
    BUS
}

// Define SpotSize enum
enum SpotSize {
    SMALL,
    MEDIUM,
    LARGE
}

// Define ParkingSpot class
class ParkingSpot {
    private final int spotNumber;
    private final SpotSize size;
    private boolean isFree;
    private Vehicle parkedVehicle;
    private final Lock lock = new ReentrantLock(); // Lock for concurrency

    public ParkingSpot(int spotNumber, SpotSize size) {
        this.spotNumber = spotNumber;
        this.size = size;
        this.isFree = true;
        this.parkedVehicle = null;
    }

    public int getSpotNumber() {
        return spotNumber;
    }

    public SpotSize getSize() {
        return size;
    }

    public boolean isFree() {
        return isFree;
    }

    public Vehicle getParkedVehicle() {
        return parkedVehicle;
    }

    public boolean lock() {
        return lock.tryLock();  //Use tryLock to avoid indefinite blocking
    }

    public void unlock() {
        lock.unlock();
    }

    public boolean assignVehicle(Vehicle vehicle) {
        if (isFree) {
            parkedVehicle = vehicle;
            isFree = false;
            return true;
        }
        return false;
    }

    public void removeVehicle() {
        parkedVehicle = null;
        isFree = true;
    }

    @Override
    public String toString() {
        return "ParkingSpot{" +
                "spotNumber=" + spotNumber +
                ", size=" + size +
                ", isFree=" + isFree +
                '}';
    }
}

// Define ParkingFloor class
class ParkingFloor {
    private final int floorNumber;
    private final List<ParkingSpot> parkingSpots;

    public ParkingFloor(int floorNumber, List<ParkingSpot> parkingSpots) {
        this.floorNumber = floorNumber;
        this.parkingSpots = parkingSpots;
    }

    public int getFloorNumber() {
        return floorNumber;
    }

    public List<ParkingSpot> getParkingSpots() {
        return parkingSpots;
    }

    // Method to find a free spot for a vehicle. Returns the spot number or -1 if no spot is available.
    public int findSpot(VehicleType vehicleType) {
        SpotSize requiredSize = getRequiredSpotSize(vehicleType);
        for (ParkingSpot spot : parkingSpots) {
            if (spot.isFree() && spot.getSize() == requiredSize) {
                return spot.getSpotNumber(); // Return the spot number
            }
        }
        return -1; // No spot available
    }


    // Method to assign a vehicle to a spot.
    public boolean assignVehicleToSpot(int spotNumber, Vehicle vehicle) {
        for (ParkingSpot spot : parkingSpots) {
            if (spot.getSpotNumber() == spotNumber) {
                 if (spot.lock()) {
                     try {
                         if (spot.isFree()) { // Double check isFree after acquiring lock
                             boolean assigned = spot.assignVehicle(vehicle);
                             if(assigned){
                                 return true;
                             }
                         }

                     } finally {
                         spot.unlock();
                     }

                 }

                return false;
            }
        }
        return false;
    }

    // Method to remove a vehicle from a spot.
    public boolean removeVehicleFromSpot(int spotNumber) {
        for (ParkingSpot spot : parkingSpots) {
            if (spot.getSpotNumber() == spotNumber) {
                if (spot.lock()) {
                    try {
                        if (!spot.isFree()) { //Double check not Free before removing.
                            spot.removeVehicle();
                            return true;
                        }
                    } finally {
                        spot.unlock();
                    }
                }
                return false;
            }
        }
        return false;
    }

    private SpotSize getRequiredSpotSize(VehicleType vehicleType) {
        return switch (vehicleType) {
            case MOTORCYCLE -> SpotSize.SMALL;
            case CAR -> SpotSize.MEDIUM;
            case BUS -> SpotSize.LARGE;
        };
    }
}

