        this.parkingFee = parkingFee;
    }

    @Override
    public String toString() {
        return "Ticket{" +
                "ticketNumber='" + ticketNumber + '\'' +
                ", vehicle=" + vehicle +
                ", spotNumber=" + spotNumber +
                ", floorNumber=" + floorNumber +
                ", entryTime=" + entryTime +
                ", exitTime=" + exitTime +
                ", parkingFee=" + parkingFee +
                '}';
    }
}


// Define ParkingLot class
class ParkingLot {
    private final List<ParkingFloor> parkingFloors;
    private final Map<String, Ticket> activeTickets;
    private final Lock lock = new ReentrantLock();  //Lock for managing ticket creation.

    public ParkingLot(List<ParkingFloor> parkingFloors) {
        this.parkingFloors = parkingFloors;
        this.activeTickets = new HashMap<>();
    }

    // Method to find an available spot for a vehicle.
    public Ticket parkVehicle(Vehicle vehicle) {
        for (ParkingFloor floor : parkingFloors) {
            int spotNumber = floor.findSpot(vehicle.getType());
            if (spotNumber != -1) {
               if(lock.tryLock()){
                   try {
                       if (floor.assignVehicleToSpot(spotNumber, vehicle)) {
                           Ticket ticket = new Ticket(vehicle, spotNumber, floor.getFloorNumber());
                           activeTickets.put(ticket.getTicketNumber(), ticket);
                           return ticket;
                       }
                   } finally {
                       lock.unlock();
                   }
               }
            }
        }
        return null; // No spot available
    }


    // Method to remove a vehicle from the parking lot.
    public Ticket unparkVehicle(String ticketNumber) {
        Ticket ticket = activeTickets.get(ticketNumber);
        if (ticket != null) {
            for (ParkingFloor floor : parkingFloors) {
                if (floor.getFloorNumber() == ticket.getFloorNumber()) {
                    if (floor.removeVehicleFromSpot(ticket.getSpotNumber())) {
                        ticket.setExitTime(LocalDateTime.now());
                        double fee = calculateParkingFee(ticket);
                        ticket.setParkingFee(fee);
                        activeTickets.remove(ticketNumber);
                        return ticket;
                    }
                }
            }
        }
        return null; // Ticket not found or vehicle could not be removed
    }

    // Method to calculate the parking fee.
    private double calculateParkingFee(Ticket ticket) {
        Duration duration = Duration.between(ticket.getEntryTime(), ticket.getExitTime());
        long hours = duration.toHours();

        // Different fee structure based on vehicle type.
        double hourlyRate;
        switch (ticket.getVehicle().getType()) {
            case MOTORCYCLE:
                hourlyRate = 2.0;
                break;
            case CAR:
                hourlyRate = 5.0;
                break;
            case BUS:
                hourlyRate = 10.0;
                break;
            default:
                hourlyRate = 0.0; // Invalid vehicle type. Should not occur.
        }

        return hours * hourlyRate;
    }

    // Method to display the available spots.
    public void displayAvailableSpots() {
        for (ParkingFloor floor : parkingFloors) {
            int smallSpots = 0;
            int mediumSpots = 0;
            int largeSpots = 0;

            for (ParkingSpot spot : floor.getParkingSpots()) {
                if (spot.isFree()) {
                    switch (spot.getSize()) {
                        case SMALL:
                            smallSpots++;
                            break;
                        case MEDIUM:
                            mediumSpots++;
                            break;
                        case LARGE:
                            largeSpots++;
                            break;
                    }
                }
            }

            System.out.println("Floor " + floor.getFloorNumber() + ":");
            System.out.println("  Small spots available: " + smallSpots);
            System.out.println("  Medium spots available: " + mediumSpots);
            System.out.println("  Large spots available: " + largeSpots);
        }
    }
}



