import java.util.*;

public class TopKFrequentElements {

    /**
     * Finds the top N most frequent elements in an array of integers.
     * The result is sorted in descending order of frequency, and if two elements have the same frequency,
     * they are sorted by their value in ascending order.
     *
     * @param nums The input array of integers.
     * @param n    The number of top frequent elements to return.
     * @return A list of integers representing the top N frequent elements, sorted as described.
     *
     * Time Complexity: O(K log K + N) where N is the number of elements in the input array and K is the number of distinct elements in the array.
     *   - O(N) to compute the frequency of each element.
     *   - O(K) to store frequencies and values into map where K <= N.
     *   - O(K log K) for sorting in priority queue of at most K elements.
     *   - O(K) for extracting from the heap.
     * Space Complexity: O(K) where K is the number of distinct elements in the input array (frequency map and priority queue). In the worst case, where all elements are distinct, K=N, so the space complexity would be O(N).
     */
    public List<Integer> findTopNFrequentElements(int[] nums, int n) {
        // 1. Count the frequency of each element using a HashMap
        Map<Integer, Integer> frequencyMap = new HashMap<>();
        for (int num : nums) {
            frequencyMap.put(num, frequencyMap.getOrDefault(num, 0) + 1);
        }

        // 2. Use a PriorityQueue (min-heap) to maintain the top N frequent elements.
        PriorityQueue<Integer> minHeap = new PriorityQueue<>(
                (a, b) -> {
                    int frequencyComparison = frequencyMap.get(a) - frequencyMap.get(b);
                    if (frequencyComparison == 0) {
                        return a - b; // Sort by value in ascending order if frequencies are equal
                    }
                    return frequencyComparison; // Sort by frequency in ascending order (min-heap)
                }
        );

        // 3. Iterate through the frequency map and add/update elements in the min-heap.
        for (int num : frequencyMap.keySet()) {
            minHeap.add(num);
            if (minHeap.size() > n) {
                minHeap.poll(); // Remove the least frequent element if the heap size exceeds N
            }
        }

        // 4. Extract elements from the min-heap and sort them in descending order of frequency (and ascending order of value if frequencies are equal).
        List<Integer> result = new ArrayList<>(minHeap);
        result.sort((a, b) -> {
            int frequencyComparison = frequencyMap.get(b) - frequencyMap.get(a); // Reverse order for descending frequency
            if (frequencyComparison == 0) {
                return a - b; // Sort by value in ascending order if frequencies are equal
            }
            return frequencyComparison;
        });

        return result;
    }

    public static void main(String[] args) {
        TopKFrequentElements topKFrequentElements = new TopKFrequentElements();

        // Test Case 1
        int[] nums1 = {3, 1, 2, 2, 4, 3, 5};
        int n1 = 2;
        List<Integer> result1 = topKFrequentElements.findTopNFrequentElements(nums1, n1);
        System.out.println("Test Case 1: " + result1); // Expected: [2, 3]

        // Test Case 2
        int[] nums2 = {1};
        int n2 = 1;
        List<Integer> result2 = topKFrequentElements.findTopNFrequentElements(nums2, n2);
        System.out.println("Test Case 2: " + result2); // Expected: [1]

        // Test Case 3
        int[] nums3 = {4, 1, 2, 2, 3, 3, 3, 4, 4, 4};
        int n3 = 3;
        List<Integer> result3 = topKFrequentElements.findTopNFrequentElements(nums3, n3);
        System.out.println("Test Case 3: " + result3); // Expected: [4, 3, 2]

        // Test Case 4 (All elements are same)
        int[] nums4 = {5, 5, 5, 5, 5};
        int n4 = 1;
        List<Integer> result4 = topKFrequentElements.findTopNFrequentElements(nums4, n4);
        System.out.println("Test Case 4: " + result4); // Expected: [5]

        // Test Case 5 (Negative numbers)
        int[] nums5 = {-1, -1, 0, 0, 0, 1, 1, 1, 1};
        int n5 = 2;
        List<Integer> result5 = topKFrequentElements.findTopNFrequentElements(nums5, n5);
        System.out.println("Test Case 5: " + result5); // Expected: [1, 0]

        // Test Case 6 (Empty array)
        int[] nums6 = {};
        int n6 = 0; // Valid as per constraint
        List<Integer> result6 = topKFrequentElements.findTopNFrequentElements(nums6, n6);
        System.out.println("Test Case 6: " + result6); // Expected: []

       // Test Case 7 (Large Array)
       int[] nums7 = new int[100000];
       Random random = new Random();
       for(int i = 0; i < nums7.length; i++) {
           nums7[i] = random.nextInt(100); // values from 0 to 99
       }
       int n7 = 10;
       List<Integer> result7 = topKFrequentElements.findTopNFrequentElements(nums7, n7);
       System.out.println("Test Case 7 (Large Array - first 10): " + result7.subList(0, Math.min(10, result7.size())));


        // Test Case 8 (Elements with same frequency)
        int[] nums8 = {1, 1, 2, 2, 3, 3};
        int n8 = 3;
        List<Integer> result8 = topKFrequentElements.findTopNFrequentElements(nums8, n8);
        System.out.println("Test Case 8: " + result8); // Expected: [1, 2, 3]

        // Test Case 9 (All unique elements, n = length of array)
        int[] nums9 = {1, 2, 3, 4, 5};
        int n9 = 5;
        List<Integer> result9 = topKFrequentElements.findTopNFrequentElements(nums9, n9);
        System.out.println("Test Case 9: " + result9); // Expected: [1, 2, 3, 4, 5]
    }
}
