import java.util.HashMap;
import java.util.HashSet;
import java.util.Map;
import java.util.Set;
import java.util.stream.Collectors;

public class DPPatternMatcher {

    private final Map<String, Boolean> memo = new HashMap<>();

    public boolean wordPatternMatch(String pattern, String str) {
        return isMatch(pattern, 0, str, 0, new HashMap<>(), new HashSet<>());
    }

    private boolean isMatch(String pattern, int pIndex, String str, int sIndex,
                            Map<Character, String> charToStr, Set<String> strUsed) {

        // Generate a unique key for the current subproblem state
        String memoKey = generateMemoKey(pIndex, sIndex, charToStr);
        if (memo.containsKey(memoKey)) {
            return memo.get(memoKey);
        }

        // Base case 1: A complete match is found when both indices reach the end.
        if (pIndex == pattern.length() && sIndex == str.length()) {
            return true;
        }

        // Base case 2: If either index reaches the end prematurely, it's not a match.
        if (pIndex == pattern.length() || sIndex == str.length()) {
            return false;
        }

        char pChar = pattern.charAt(pIndex);

        // Case 1: The pattern character has an existing mapping.
        if (charToStr.containsKey(pChar)) {
            String mappedStr = charToStr.get(pChar);
            if (str.startsWith(mappedStr, sIndex)) {
                boolean result = isMatch(pattern, pIndex + 1, str, sIndex + mappedStr.length(), charToStr, strUsed);
                memo.put(memoKey, result);
                return result;
            }
            memo.put(memoKey, false);
            return false;
        }

        // Case 2: The pattern character does not have a mapping yet.
        for (int i = sIndex; i < str.length(); i++) {
            String currentSubstring = str.substring(sIndex, i + 1);

            if (strUsed.contains(currentSubstring)) {
                continue;
            }

            charToStr.put(pChar, currentSubstring);
            strUsed.add(currentSubstring);

            if (isMatch(pattern, pIndex + 1, str, i + 1, charToStr, strUsed)) {
                memo.put(memoKey, true);
                return true;
            }

            // Backtrack: Undo the mapping and try the next possible substring.
            strUsed.remove(currentSubstring);
            charToStr.remove(pChar);
        }

        memo.put(memoKey, false);
        return false;
    }

    private String generateMemoKey(int pIndex, int sIndex, Map<Character, String> charToStr) {
        // Create a stable string representation of the map state.
        String mapState = charToStr.entrySet().stream()
                .sorted(Map.Entry.comparingByKey())
                .map(entry -> entry.getKey() + ":" + entry.getValue())
                .collect(Collectors.joining(","));
        return pIndex + "-" + sIndex + "-" + mapState;
    }

    public static void main(String[] args) {
        DPPatternMatcher matcher = new DPPatternMatcher();

        String pattern1 = "aabab";
        String str1 = "catcatdogcatdog";
        System.out.println("Pattern: " + pattern1 + ", String: " + str1);
        System.out.println("Result: " + matcher.wordPatternMatch(pattern1, str1));

        String pattern2 = "abba";
        String str2 = "redblueredblue";
        System.out.println("Pattern: " + pattern2 + ", String: " + str2);
        System.out.println("Result: " + matcher.wordPatternMatch(pattern2, str2));

        String pattern3 = "aaaa";
        String str3 = "dogcatdogcat";
        System.out.println("Pattern: " + pattern3 + ", String: " + str3);
        System.out.println("Result: " + matcher.wordPatternMatch(pattern3, str3));
    }
}

