import java.util.*;

public class JsonParser {

    /**
     * Parses a JSON string and returns a representation of the JSON data.
     *
     * @param jsonString The JSON string to parse.
     * @return A Map or List representing the parsed JSON data.  Returns null if the string is "null".
     * @throws IllegalArgumentException If the input is not valid JSON.
     */
    public static Object parseJson(String jsonString) {
        if (jsonString == null || jsonString.trim().isEmpty()) {
            throw new IllegalArgumentException("JSON string cannot be null or empty.");
        }

        jsonString = jsonString.trim();

        if (jsonString.equals("null")) {
            return null; // Handle explicit null values
        }


        if (jsonString.startsWith("{")) {
            return parseJsonObject(jsonString);
        } else if (jsonString.startsWith("[")) {
            return parseJsonArray(jsonString);
        } else {
            return parseJsonPrimitive(jsonString);
        }
    }

    /**
     * Parses a JSON object string into a Map.
     *
     * @param jsonString The JSON object string.
     * @return A Map representing the parsed JSON object.
     * @throws IllegalArgumentException If the input is not a valid JSON object.
     */
    private static Map<String, Object> parseJsonObject(String jsonString) {
        Map<String, Object> jsonObject = new LinkedHashMap<>(); // Use LinkedHashMap to preserve order

        // Remove curly braces
        jsonString = jsonString.substring(1, jsonString.length() - 1).trim();

        if (jsonString.isEmpty()) {
            return jsonObject; // Empty JSON object
        }

        String[] keyValuePairs = splitKeyValuePairs(jsonString);

        for (String keyValuePair : keyValuePairs) {
            String[] parts = keyValuePair.split(":", 2); // Split into key and value (only first occurrence)

            if (parts.length != 2) {
                throw new IllegalArgumentException("Invalid JSON object format: " + keyValuePair);
            }

            String key = parts[0].trim();
            String value = parts[1].trim();

            // Remove quotes from the key
            if (key.startsWith("\"") && key.endsWith("\"")) {
                key = key.substring(1, key.length() - 1);
            } else {
                throw new IllegalArgumentException("Invalid JSON key format: " + key);
            }

            jsonObject.put(key, parseJson(value)); // Recursively parse the value
        }

        return jsonObject;
    }

    /**
     * Splits a JSON object string into key-value pairs, handling nested objects and arrays.
     *
     * @param jsonString The JSON object string.
     * @return An array of key-value pairs.
     */
    private static String[] splitKeyValuePairs(String jsonString) {
        List<String> keyValuePairs = new ArrayList<>();
        int braceCount = 0;
        int bracketCount = 0;
        StringBuilder currentPair = new StringBuilder();

        for (int i = 0; i < jsonString.length(); i++) {
            char c = jsonString.charAt(i);

            if (c == '{') {
                braceCount++;
            } else if (c == '}') {
                braceCount--;
            } else if (c == '[') {
                bracketCount++;
            } else if (c == ']') {
                bracketCount--;
            }

            if (c == ',' && braceCount == 0 && bracketCount == 0) {
                keyValuePairs.add(currentPair.toString().trim());
                currentPair = new StringBuilder();
            } else {
                currentPair.append(c);
            }
        }

        keyValuePairs.add(currentPair.toString().trim());
        return keyValuePairs.toArray(new String[0]);
    }


    /**
     * Parses a JSON array string into a List.
     *
     * @param jsonString The JSON array string.
     * @return A List representing the parsed JSON array.
     * @throws IllegalArgumentException If the input is not a valid JSON array.
     */
    private static List<Object> parseJsonArray(String jsonString) {
        List<Object> jsonArray = new ArrayList<>();

        // Remove square brackets
        jsonString = jsonString.substring(1, jsonString.length() - 1).trim();

        if (jsonString.isEmpty()) {
            return jsonArray; // Empty JSON array
        }

        String[] elements = splitArrayElements(jsonString);

        for (String element : elements) {
            jsonArray.add(parseJson(element.trim())); // Recursively parse the element
        }

        return jsonArray;
    }

    /**
     * Splits a JSON array string into elements, handling nested objects and arrays.
     *
     * @param jsonString The JSON array string.
     * @return An array of elements.
     */
    private static String[] splitArrayElements(String jsonString) {
        List<String> elements = new ArrayList<>();
        int braceCount = 0;
        int bracketCount = 0;
        StringBuilder currentElement = new StringBuilder();

        for (int i = 0; i < jsonString.length(); i++) {
            char c = jsonString.charAt(i);

            if (c == '{') {
                braceCount++;
            } else if (c == '}') {
                braceCount--;
            } else if (c == '[') {
                bracketCount++;
            } else if (c == ']') {
                bracketCount--;
            }

            if (c == ',' && braceCount == 0 && bracketCount == 0) {
                elements.add(currentElement.toString().trim());
                currentElement = new StringBuilder();
            } else {
                currentElement.append(c);
            }
        }

        elements.add(currentElement.toString().trim());
        return elements.toArray(new String[0]);
    }


    /**
     * Parses a JSON primitive (String, Number, Boolean, or null) string.
     *
     * @param jsonString The JSON primitive string.
     * @return The parsed JSON primitive.
     * @throws IllegalArgumentException If the input is not a valid JSON primitive.
     */
    private static Object parseJsonPrimitive(String jsonString) {
        if (jsonString.startsWith("\"") && jsonString.endsWith("\"")) {
            // String
            return jsonString.substring(1, jsonString.length() - 1);
        } else if (jsonString.equalsIgnoreCase("true")) {
            // Boolean (true)
            return true;
        } else if (jsonString.equalsIgnoreCase("false")) {
            // Boolean (false)
            return false;
        } else if (jsonString.equalsIgnoreCase("null")) {
            return null;
        } else {
            // Number (try to parse as Integer or Double)
            try {
                return Integer.parseInt(jsonString);
            } catch (NumberFormatException e1) {
                try {
                    return Double.parseDouble(jsonString);
                } catch (NumberFormatException e2) {
                    throw new IllegalArgumentException("Invalid JSON primitive: " + jsonString);
                }
            }
        }
    }

