import java.util.HashSet;
import java.util.Set;

public class SubarrayWithZeroSum {

    /**
     * Checks if a subarray with a sum of 0 exists in the given array.
     *
     * @param arr The input array of integers.
     * @return True if a subarray with sum 0 exists, false otherwise.
     *
     * Algorithm Explanation:
     * 1.  Initialize a set `seenSums` to store the cumulative sums encountered so far.
     * 2.  Initialize a variable `currentSum` to 0.
     * 3.  Iterate through the array:
     *     a. Add the current element to `currentSum`.
     *     b. If `currentSum` is 0, it means the subarray from the beginning of the array to the current element has a sum of 0.  Return `true`.
     *     c. If `currentSum` is already present in `seenSums`, it means that the sum of elements between the previous occurrence of `currentSum` and the current element is 0.  Return `true`.
     *     d. Add `currentSum` to `seenSums`.
     * 4.  If the loop completes without finding a subarray with sum 0, return `false`.
     */
    public static boolean hasSubarrayWithZeroSum(int[] arr) {
        // Use a set to store previously seen prefix sums.
        Set<Integer> seenSums = new HashSet<>();

        // Initialize the current sum.
        int currentSum = 0;

        // Iterate through the array.
        for (int num : arr) {
            currentSum += num;

            // Check if the current sum is 0 (meaning a subarray from the beginning has sum 0).
            if (currentSum == 0) {
                return true;
            }

            // Check if the current sum has been seen before. If so, there's a subarray with sum 0.
            if (seenSums.contains(currentSum)) {
                return true;
            }

            // Add the current sum to the set of seen sums.
            seenSums.add(currentSum);
        }

        // If no subarray with sum 0 is found, return false.
        return false;
    }

    public static void main(String[] args) {
        // Test cases demonstrating the solution

        // Test case 1: Basic test case with a subarray that sums to zero
        int[] arr1 = {4, 2, -3, 1, 6};
        System.out.println("Array: " + arrayToString(arr1) + ", Has subarray with zero sum: " + hasSubarrayWithZeroSum(arr1)); // Expected: true

        // Test case 2: Array with all positive numbers (no subarray with sum 0)
        int[] arr2 = {1, 2, 3, 4, 5};
        System.out.println("Array: " + arrayToString(arr2) + ", Has subarray with zero sum: " + hasSubarrayWithZeroSum(arr2)); // Expected: false

        // Test case 3: Array with a single element equal to zero
        int[] arr3 = {0};
        System.out.println("Array: " + arrayToString(arr3) + ", Has subarray with zero sum: " + hasSubarrayWithZeroSum(arr3)); // Expected: true

        // Test case 4: Array with multiple zeros
        int[] arr4 = {1, 2, 0, 4, 0, 6};
        System.out.println("Array: " + arrayToString(arr4) + ", Has subarray with zero sum: " + hasSubarrayWithZeroSum(arr4)); // Expected: true

        // Test case 5: Array with only negative numbers (no subarray with sum 0)
        int[] arr5 = {-1, -2, -3, -4, -5};
        System.out.println("Array: " + arrayToString(arr5) + ", Has subarray with zero sum: " + hasSubarrayWithZeroSum(arr5)); // Expected: false

        // Test case 6: Array with a subarray that sums to zero at the beginning
        int[] arr6 = {-1, 1, 2, 3, 4};
        System.out.println("Array: " + arrayToString(arr6) + ", Has subarray with zero sum: " + hasSubarrayWithZeroSum(arr6)); // Expected: true

        // Test case 7: Empty array
        int[] arr7 = {};
        System.out.println("Array: " + arrayToString(arr7) + ", Has subarray with zero sum: " + hasSubarrayWithZeroSum(arr7)); // Expected: false

        // Test case 8: Array with large numbers that sum to 0
        int[] arr8 = {Integer.MAX_VALUE, Integer.MIN_VALUE};
        System.out.println("Array: " + arrayToString(arr8) + ", Has subarray with zero sum: " + hasSubarrayWithZeroSum(arr8)); // Expected: true

        // Test case 9: Array with complex negative and positive numbers.
        int[] arr9 = {1, -1, 0, 2, -2};
        System.out.println("Array: " + arrayToString(arr9) + ", Has subarray with zero sum: " + hasSubarrayWithZeroSum(arr9)); // Expected: true

        // Test case 10: Array with same numbers repeating with an added negative counterpart.
        int[] arr10 = {1, 2, 3, -1, -2, -3};
        System.out.println("Array: " + arrayToString(arr10) + ", Has subarray with zero sum: " + hasSubarrayWithZeroSum(arr10)); // Expected: true
    }

    /**
     * Helper method to convert an integer array to a string for easier printing.
     * @param arr The input array.
     * @return The array's string representation.
     */
    private static String arrayToString(int[] arr) {
        if (arr == null || arr.length == 0) {
            return "[]";
        }
        StringBuilder sb = new StringBuilder();
        sb.append("[");
        for (int i = 0; i < arr.length; i++) {
            sb.append(arr[i]);
            if (i < arr.length - 1) {
                sb.append(", ");
            }
        }
        sb.append("]");
        return sb.toString();
    }

    /*
     * Time Complexity: O(n), where n is the length of the array. We iterate through the array once. The HashSet operations (add and contains) take O(1) time on average.
     * Space Complexity: O(n), where n is the length of the array. In the worst case, the HashSet could store all the prefix sums, which would require O(n) space.
     */
}
