import java.util.Comparator;
import java.util.PriorityQueue;
import java.util.Queue;
import java.util.Random;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import java.util.concurrent.TimeUnit;
import java.util.concurrent.atomic.AtomicLong;

public class SmsScheduler {

    // Enum to represent message priority
    public enum Priority {
        HIGH,   // OTP
        MEDIUM, // Delivery Notifications
        LOW     // Promotional Messages (Not used in this specific problem but included for completeness)
    }

    // Class to represent an SMS message
    static class SmsMessage {
        private final long messageId;
        private final String phoneNumber;
        private final String messageContent;
        private final Priority priority;
        private final long creationTimestamp;
        private SmsStatus status;
        private int retryCount;

        public SmsMessage(long messageId, String phoneNumber, String messageContent, Priority priority) {
            this.messageId = messageId;
            this.phoneNumber = phoneNumber;
            this.messageContent = messageContent;
            this.priority = priority;
            this.creationTimestamp = System.currentTimeMillis();
            this.status = SmsStatus.PENDING;
            this.retryCount = 0;
        }

        public long getMessageId() {
            return messageId;
        }

        public String getPhoneNumber() {
            return phoneNumber;
        }

        public String getMessageContent() {
            return messageContent;
        }

        public Priority getPriority() {
            return priority;
        }

        public long getCreationTimestamp() {
            return creationTimestamp;
        }

        public SmsStatus getStatus() {
            return status;
        }

        public void setStatus(SmsStatus status) {
            this.status = status;
        }

        public int getRetryCount() {
            return retryCount;
        }

        public void incrementRetryCount() {
            this.retryCount++;
        }

        @Override
        public String toString() {
            return "SmsMessage{" +
                    "messageId=" + messageId +
                    ", phoneNumber='" + phoneNumber + '\'' +
                    ", messageContent='" + messageContent + '\'' +
                    ", priority=" + priority +
                    ", creationTimestamp=" + creationTimestamp +
                    ", status=" + status +
                    ", retryCount=" + retryCount +
                    '}';
        }
    }

    // Enum to represent SMS message status
    public enum SmsStatus {
        PENDING,
        SENT,
        FAILED,
        RETRYING
    }

    private final PriorityQueue<SmsMessage> messageQueue; // Priority queue for message scheduling
    private final ExecutorService executorService; // Thread pool for sending messages concurrently
    private final int numThreads; // Number of threads in the thread pool
    private final Random random = new Random(); // For simulating SMS gateway failures.
    private final AtomicLong messageIdCounter = new AtomicLong(0); // Atomic counter for generating unique message IDs.

    private final int maxRetries; //Maximum retries per message before failure.

    // Constructor to initialize the SMS scheduler
    public SmsScheduler(int numThreads, int maxRetries) {
        this.numThreads = numThreads;
        this.executorService = Executors.newFixedThreadPool(numThreads);
        // Priority queue with custom comparator to prioritize messages based on priority and creation timestamp
        this.messageQueue = new PriorityQueue<>(Comparator
                .comparing(SmsMessage::getPriority, Comparator.comparingInt(Priority::ordinal))
                .thenComparing(SmsMessage::getCreationTimestamp));
        this.maxRetries = maxRetries;
    }

    // Method to submit an SMS message to the scheduler
    public void submitMessage(SmsMessage message) {
        if (message.getPriority() == Priority.HIGH) {
            // Send high-priority messages immediately on a separate thread.  Avoids queueing.
            executorService.submit(() -> processMessage(message));
        } else {
            // Add medium and low priority messages to the queue
            synchronized (messageQueue) {
                messageQueue.offer(message);
                messageQueue.notifyAll(); // Notify waiting threads that a new message is available
            }
        }
    }

    // Method to start the SMS scheduler
    public void start() {
        // Create and start worker threads to process messages from the queue
        for (int i = 0; i < numThreads; i++) {
            executorService.submit(this::processQueue);
        }
    }

    // Worker thread to process messages from the queue
    private void processQueue() {
        while (true) {
            try {
                SmsMessage message;
                synchronized (messageQueue) {
                    // Wait if the queue is empty
                    while (messageQueue.isEmpty()) {
                        messageQueue.wait();
                    }
                    message = messageQueue.poll(); // Retrieve the highest priority message from the queue
                }

                processMessage(message);

            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
                return; // Exit the loop if the thread is interrupted
            }
        }
    }

    // Method to simulate sending an SMS message
    private void processMessage(SmsMessage message) {
        if (message == null) {
            return;
        }
        try {
            message.setStatus(SmsStatus.RETRYING); //Update status to Retrying

            // Simulate sending the SMS message (replace with actual SMS gateway integration)
            boolean success = simulateSmsSending(message);

            if (success) {
                message.setStatus(SmsStatus.SENT);
                System.out.println("Sent: " + message);
            } else {
                // Retry mechanism with a limited number of retries.
                if (message.getRetryCount() < maxRetries) {
                    message.incrementRetryCount();
                    message.setStatus(SmsStatus.RETRYING);
                    System.out.println("Retrying: " + message);
                    //Resubmit to the queue for retry. Introduce a small delay.  High priority messages shouldn't be re-queued
                    Thread.sleep(100 * message.getRetryCount()); // Exponential backoff
                    if (message.getPriority() == Priority.HIGH) {
                        executorService.submit(() -> processMessage(message));
                    }
                    else{
                        submitMessage(message);
                    }
                } else {
                    message.setStatus(SmsStatus.FAILED);
                    System.err.println("Failed after retries: " + message);
                }
            }
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt(); // Restore interrupted state.
            System.err.println("Interrupted while processing message: " + message);
        }
    }


    // Simulate SMS sending with potential failure (replace with actual SMS gateway integration)
    private boolean simulateSmsSending(SmsMessage message) throws InterruptedException {
        // Introduce a small delay to simulate network latency
        Thread.sleep(50 + random.nextInt(100));

        // Simulate a 10% chance of failure
        if (random.nextInt(10) == 0) {
            System.err.println("Simulated SMS Sending Failure for message: " + message.getMessageId());
            return false;
        }

        return true;
    }

    // Method to shutdown the SMS scheduler gracefully
    public void shutdown() {
        executorService.shutdown();
        try {
            if (!executorService.awaitTermination(5, TimeUnit.SECONDS)) {
                executorService.shutdownNow(); // Force shutdown if tasks are still running
            }
        } catch (InterruptedException e) {
            executorService.shutdownNow();
            Thread.currentThread().interrupt();
        }
    }

    public long getNextMessageId() {
        return messageIdCounter.incrementAndGet();
    }

    public static void main(String[] args) throws InterruptedException {
        int numThreads = 5;
        int maxRetries = 3;
        SmsScheduler scheduler = new SmsScheduler(numThreads, maxRetries);
        scheduler.start(); // Start processing SMS messages

        // Test cases
        //Test case 1: OTP is delivered immediately when delivery notifications are also being sent
        System.out.println("Test Case 1: OTP bypassing delivery notifications");
        for (int i = 0; i < 10; i++) {
            long messageId = scheduler.getNextMessageId();
            scheduler.submitMessage(new SmsMessage(messageId, "1234567890", "Delivery update " + i, Priority.MEDIUM));
        }
        long otpMessageId = scheduler.getNextMessageId();
        scheduler.submitMessage(new SmsMessage(otpMessageId, "0987654321", "OTP for login", Priority.HIGH)); // OTP should be delivered before medium messages
        Thread.sleep(100); //Sufficient time for all messages to be delivered

        //Test case 2: Retry mechanism is working appropriately without interfering with OTP messages
        System.out.println("\nTest Case 2: Retry Mechanism with OTP");
        long failedMessageId = scheduler.getNextMessageId();
        scheduler.submitMessage(new SmsMessage(failedMessageId, "1122334455", "This message is designed to fail initially.", Priority.MEDIUM));

        long otpMessageId2 = scheduler.getNextMessageId();
        scheduler.submitMessage(new SmsMessage(otpMessageId2, "5544332211", "Urgent OTP", Priority.HIGH));
        Thread.sleep(100);

        //Test case 3: High volume of medium and low priority messages, OTPs must still be delivered in seconds
        System.out.println("\nTest Case 3: High Volume and OTP Delivery");
        for (int i = 0; i < 100; i++) {
            long messageId = scheduler.getNextMessageId();
            scheduler.submitMessage(new SmsMessage(messageId, "1234567" + i, "Delivery update " + i, Priority.MEDIUM));
        }

        for (int i = 0; i < 5; i++) {
            long otpMessageId3 = scheduler.getNextMessageId();
            scheduler.submitMessage(new SmsMessage(otpMessageId3, "99887766" + i, "Another OTP " + i, Priority.HIGH));
        }
        Thread.sleep(200);

        //Test case 4: Empty queue handling.

        System.out.println("\nTest Case 4: Empty Queue Handling");
        Thread.sleep(100); //Let the queue drain.

        //Test Case 5: Edge case where message is null.

        System.out.println("\nTest Case 5: Handling null message");
        scheduler.processMessage(null);

        Thread.sleep(1000); // Allow time for all messages to be processed.
        scheduler.shutdown(); // Shut down the executor service gracefully.
    }
}

/*
Time Complexity Analysis:

submitMessage(): O(1) for HIGH priority, O(log N) for MEDIUM/LOW due to priority queue insertion.
processQueue(): O(log N) for polling from the priority queue. The loop runs indefinitely, but the wait() operation blocks the thread until a new message is available.
processMessage(): O(1) excluding the simulateSmsSending.  Retry logic adds a delay, but the number of retries is bounded.
simulateSmsSending(): O(1).
shutdown(): Depends on the executor service's termination. In the worst case, it waits for the specified timeout before forcing shutdown.

Overall:
- Submitting a message: O(1) for high priority, O(log N) for medium/low.
- Processing messages: O(log N) per message.

Space Complexity Analysis:

messageQueue: O(N), where N is the number of messages in the queue. In the worst case, all messages could be in the queue at once.
executorService: O(T), where T is the number of threads in the thread pool.
Other variables: O(1).

Overall: O(N + T)
*/
