public class DutchNationalFlag {

    /**
     * Sorts the array in-place such that 0s come first, then 1s, and then 2s.
     *
     * @param arr The array to be sorted.
     */
    public static void sort012(int[] arr) {
        // Time Complexity: O(N), where N is the length of the array.
        // Space Complexity: O(1), as it is an in-place algorithm.

        int low = 0;       // Points to the beginning of the 0s section
        int mid = 0;       // Points to the current element being examined
        int high = arr.length - 1;  // Points to the end of the 2s section

        // Iterate until mid crosses high
        while (mid <= high) {
            switch (arr[mid]) {
                case 0:
                    // Swap arr[low] and arr[mid] and increment both pointers
                    swap(arr, low, mid);
                    low++;
                    mid++;
                    break;
                case 1:
                    // No swap needed, just move to the next element
                    mid++;
                    break;
                case 2:
                    // Swap arr[mid] and arr[high] and decrement the high pointer
                    swap(arr, mid, high);
                    high--;
                    break;
            }
        }
    }

    /**
     * Helper function to swap two elements in an array.
     *
     * @param arr The array in which elements need to be swapped.
     * @param i   The index of the first element.
     * @param j   The index of the second element.
     */
    private static void swap(int[] arr, int i, int j) {
        int temp = arr[i];
        arr[i] = arr[j];
        arr[j] = temp;
    }

    public static void main(String[] args) {
        // Test cases
        int[] arr1 = {0, 1, 2, 0, 1, 2};
        System.out.println("Original Array: " + java.util.Arrays.toString(arr1));
        sort012(arr1);
        System.out.println("Sorted Array: " + java.util.Arrays.toString(arr1)); // Expected: [0, 0, 1, 1, 2, 2]

        int[] arr2 = {2, 0, 1};
        System.out.println("Original Array: " + java.util.Arrays.toString(arr2));
        sort012(arr2);
        System.out.println("Sorted Array: " + java.util.Arrays.toString(arr2)); // Expected: [0, 1, 2]

        int[] arr3 = {2, 2, 2, 2, 2};
        System.out.println("Original Array: " + java.util.Arrays.toString(arr3));
        sort012(arr3);
        System.out.println("Sorted Array: " + java.util.Arrays.toString(arr3)); // Expected: [2, 2, 2, 2, 2]

        int[] arr4 = {0, 0, 0, 0, 0};
        System.out.println("Original Array: " + java.util.Arrays.toString(arr4));
        sort012(arr4);
        System.out.println("Sorted Array: " + java.util.Arrays.toString(arr4)); // Expected: [0, 0, 0, 0, 0]

        int[] arr5 = {1, 1, 1, 1, 1};
        System.out.println("Original Array: " + java.util.Arrays.toString(arr5));
        sort012(arr5);
        System.out.println("Sorted Array: " + java.util.Arrays.toString(arr5)); // Expected: [1, 1, 1, 1, 1]

        int[] arr6 = {0, 1, 2, 1, 0, 2, 1, 0, 2, 1, 0};
