import java.util.*;

public class AlienLanguageOrder {

    /**
     * Finds the order of characters in an alien language given a sorted dictionary of words.
     *
     * @param dict An array of strings representing the sorted dictionary of alien words.
     * @param K    The number of distinct characters in the alien language (not strictly needed, but good practice).
     * @return A string representing the order of characters in the alien language, or an empty string if no valid order can be determined.
     *
     * Time Complexity: O(V + E), where V is the number of distinct characters and E is the number of dependencies (edges) between characters.
     * Space Complexity: O(V), where V is the number of distinct characters to store the graph, in-degree, and result.
     */
    public String findOrder(String[] dict, int K) {
        // 1. Build the graph and in-degree map.
        Map<Character, List<Character>> graph = new HashMap<>();
        Map<Character, Integer> inDegree = new HashMap<>();

        // Initialize graph and in-degree for all characters present in the dictionary.
        for (String word : dict) {
            for (char c : word.toCharArray()) {
                graph.putIfAbsent(c, new ArrayList<>());
                inDegree.putIfAbsent(c, 0);
            }
        }

        // Find dependencies between characters by comparing adjacent words in the dictionary.
        for (int i = 0; i < dict.length - 1; i++) {
            String word1 = dict[i];
            String word2 = dict[i + 1];
            int len = Math.min(word1.length(), word2.length());

            for (int j = 0; j < len; j++) {
                char char1 = word1.charAt(j);
                char char2 = word2.charAt(j);

                if (char1 != char2) {
                    if (!graph.get(char1).contains(char2)) { // Avoid adding duplicate edges
                        graph.get(char1).add(char2);
                        inDegree.put(char2, inDegree.get(char2) + 1);
                    }
                    break; // Only the first different character matters for the dependency
                }
                //Handle edge case where "abc" comes before "ab" in dict. If the words are same for prefix, and first word is longer, then order is invalid.
                if(j == len - 1 && word1.length() > word2.length()){
                    return ""; // Invalid input
                }
            }
        }

        // 2. Topological Sort using Kahn's Algorithm (BFS).
        Queue<Character> queue = new LinkedList<>();
        for (char c : inDegree.keySet()) {
            if (inDegree.get(c) == 0) {
                queue.offer(c);
            }
        }

        StringBuilder result = new StringBuilder();
        int visitedCount = 0;

        while (!queue.isEmpty()) {
            char c = queue.poll();
            result.append(c);
            visitedCount++;

            for (char neighbor : graph.getOrDefault(c, Collections.emptyList())) {
                inDegree.put(neighbor, inDegree.get(neighbor) - 1);
                if (inDegree.get(neighbor) == 0) {
                    queue.offer(neighbor);
                }
            }
        }

        // 3. Check for cycle. If the number of visited characters is not equal to the total number of unique chars, then there is a cycle
        if (visitedCount != inDegree.size()) {
            return ""; // Cycle detected, no valid order exists.
        }

        return result.toString();
    }

    public static void main(String[] args) {
        AlienLanguageOrder alienOrder = new AlienLanguageOrder();

        // Test case 1
        String[] dict1 = {"baa", "abcd", "abca", "cab", "cad"};
        int K1 = 4;
        String order1 = alienOrder.findOrder(dict1, K1);
        System.out.println("Order 1: " + order1); // Expected output: bdac

        // Test case 2
        String[] dict2 = {"caa", "aaa", "aab"};
        int K2 = 3;
        String order2 = alienOrder.findOrder(dict2, K2);
        System.out.println("Order 2: " + order2); // Expected output: cab

        // Test case 3 (Cycle)
        String[] dict3 = {"z", "x", "z"};
        int K3 = 2;
        String order3 = alienOrder.findOrder(dict3, K3);
        System.out.println("Order 3 (Cycle): " + order3); // Expected output: ""

        // Test case 4 (Single character)
        String[] dict4 = {"a"};
        int K4 = 1;
        String order4 = alienOrder.findOrder(dict4, K4);
        System.out.println("Order 4 (Single character): " + order4); // Expected output: a

        // Test case 5 (Empty dictionary)
        String[] dict5 = {};
        int K5 = 0;
        String order5 = alienOrder.findOrder(dict5, K5);
        System.out.println("Order 5 (Empty dictionary): " + order5); // Expected output: ""

        // Test case 6 (Edge case: "abc" before "ab") - Invalid order
        String[] dict6 = {"abc", "ab"};
        int K6 = 3;
        String order6 = alienOrder.findOrder(dict6, K6);
        System.out.println("Order 6 (Invalid order): " + order6); // Expected output: ""

        // Test case 7 (Multiple characters)
        String[] dict7 = {"wrt", "wrf", "er", "ett", "rftt"};
        int K7 = 5;
        String order7 = alienOrder.findOrder(dict7, K7);
        System.out.println("Order 7: " + order7); // Expected output: wertf

        // Test case 8: Duplicate words
        String[] dict8 = {"wrt", "wrt", "wrf", "er", "ett", "rftt"};
        int K8 = 5;
        String order8 = alienOrder.findOrder(dict8, K8);
        System.out.println("Order 8 (Duplicates): " + order8); // Expected output: wertf

         //Test case 9 : "ab", "adc" example from leetcode
