import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

public class WordSquare {

    /**
     * Finds all possible word squares from a given list of four distinct strings.
     *
     * @param words A list of four distinct strings, each of length four.
     * @return A list of lists, where each inner list represents a valid word square.
     *         Returns an empty list if no word square can be formed.
     */
    public List<List<String>> findWordSquares(String[] words) {
        List<List<String>> result = new ArrayList<>();
        List<String> currentSquare = new ArrayList<>();
        boolean[] used = new boolean[words.length];  // Keep track of which words are already used

        findWordSquaresRecursive(words, used, currentSquare, result);
        return result;
    }

    /**
     * Recursive helper function to find word squares.
     *
     * @param words         The input list of words.
     * @param used          An array to track which words are currently used in the square.
     * @param currentSquare The current arrangement of words being explored.
     * @param result        The list to store valid word squares.
     */
    private void findWordSquaresRecursive(String[] words, boolean[] used, List<String> currentSquare, List<List<String>> result) {
        if (currentSquare.size() == 4) {
            // Base case: We have four words. Check if it's a valid word square.
            if (isValidWordSquare(currentSquare)) {
                result.add(new ArrayList<>(currentSquare)); // Add a *copy* to the result
            }
            return;
        }

        // Try each word to see if it can be added to the current square.
        for (int i = 0; i < words.length; i++) {
            if (!used[i]) {
                used[i] = true; // Mark the word as used.
                currentSquare.add(words[i]);

                // Recursive call with the new word added.
                findWordSquaresRecursive(words, used, currentSquare, result);

                // Backtrack: Remove the word and mark it as unused to explore other possibilities.
                currentSquare.remove(currentSquare.size() - 1);
                used[i] = false;
            }
        }
    }

    /**
     * Checks if a given list of four strings forms a valid word square.
     *
     * @param square A list of four strings, each of length four.
     * @return True if the strings form a valid word square, false otherwise.
     */
    private boolean isValidWordSquare(List<String> square) {
        for (int i = 0; i < 4; i++) {
            StringBuilder column = new StringBuilder();
            for (int j = 0; j < 4; j++) {
                column.append(square.get(j).charAt(i));
            }
            if (!square.get(i).equals(column.toString())) {
                return false;
            }
        }
        return true;
    }


    public static void main(String[] args) {
        WordSquare wordSquare = new WordSquare();

        // Test Case 1: Valid word square
        String[] words1 = {"BALL", "AREA", "LEAD", "LADY"};
        List<List<String>> squares1 = wordSquare.findWordSquares(words1);
        System.out.println("Test Case 1:");
        printWordSquares(squares1); //Expected output: BALL\nAREA\nLEAD\nLADY


        // Test Case 2: No valid word square
        String[] words2 = {"ABLE", "BASE", "LION", "EATS"};
        List<List<String>> squares2 = wordSquare.findWordSquares(words2);
        System.out.println("Test Case 2:");
        printWordSquares(squares2); //Expected output: (nothing)

        // Test Case 3: Another valid word square
        String[] words3 = {"WORD", "ORCA", "RODE", "DARE"};
        List<List<String>> squares3 = wordSquare.findWordSquares(words3);
        System.out.println("Test Case 3:");
        printWordSquares(squares3); // Expected output: WORD\nORCA\nRODE\nDARE

        // Test Case 4: Words with repeating characters and single valid square.
        String[] words4 = {"AAAA", "AAAA", "AAAA", "AAAA"};
        List<List<String>> squares4 = wordSquare.findWordSquares(words4);
        System.out.println("Test Case 4:");
        printWordSquares(squares4);

        // Test Case 5: One valid square among permutations.
        String[] words5 = {"MIST", "ILES", "SAIL", "TEAS"};
        List<List<String>> squares5 = wordSquare.findWordSquares(words5);
        System.out.println("Test Case 5:");
        printWordSquares(squares5);

        // Test Case 6: Edge case - Empty Input Array
        String[] words6 = {};
        List<List<String>> squares6 = wordSquare.findWordSquares(words6);
        System.out.println("Test Case 6 (Empty input):");
        printWordSquares(squares6);

        // Test Case 7: Null Input Array
        String[] words7 = null;
        List<List<String>> squares7 = null;
        try {
            squares7 = wordSquare.findWordSquares(words7);
        } catch (NullPointerException e) {
            System.out.println("Test Case 7 (Null input): NullPointerException caught.");
        }


    }

    /**
     * Helper method to print the word squares.
     *
     * @param squares A list of lists, where each inner list represents a valid word square.
     */
    private static void printWordSquares(List<List<String>> squares) {
        if (squares == null || squares.isEmpty()) {
            return;
        }
        for (List<String> square : squares) {
            for (String row : square) {
                System.out.println(row);
            }
            System.out.println(); // Add a blank line between squares
        }
    }
}

