public class ReverseLinkedList {

    // Node class for the linked list
    static class ListNode {
        int val;
        ListNode next;

        ListNode(int val) {
            this.val = val;
            this.next = null;
        }
    }

    /**
     * Reverses a singly linked list.
     *
     * @param head The head of the linked list.
     * @return The head of the reversed linked list.  Returns null if the input is null.
     *
     * Time Complexity: O(N), where N is the number of nodes in the linked list,
     *                  as we iterate through the list once.
     * Space Complexity: O(1), as we use a constant amount of extra space regardless of
     *                   the size of the linked list.
     */
    public static ListNode reverseList(ListNode head) {
        ListNode prev = null;
        ListNode current = head;
        ListNode next = null;

        while (current != null) {
            next = current.next;  // Store the next node
            current.next = prev;  // Reverse the pointer
            prev = current;       // Move prev one step forward
            current = next;       // Move current one step forward
        }

        return prev; // prev is the new head after reversing
    }


    /**
     * Reverses a sub-list within the linked list from start to end positions (inclusive).
     *
     * @param head  The head of the linked list.
     * @param start The starting position of the sub-list to be reversed (1-based index).
     * @param end   The ending position of the sub-list to be reversed (1-based index).
     * @return The head of the linked list with the sub-list reversed.
     *         Returns the original head if the list is empty or invalid input is provided.
     *
     * Time Complexity: O(N), where N is the number of nodes in the linked list,
     *                  due to traversal to find the sublist's start and end. The reversal itself is O(end - start + 1), but it's still bounded by O(N) in the worst case.
     * Space Complexity: O(1), as we use a constant amount of extra space regardless of the
     *                   size of the linked list or the sub-list being reversed.
     */
    public static ListNode reverseSubList(ListNode head, int start, int end) {
        if (head == null || start >= end) {
            return head; // Nothing to reverse
        }

        ListNode dummy = new ListNode(0); // Dummy node for easier handling of head reversal
        dummy.next = head;
        ListNode pre = dummy;

        // Move pre to the node before the start of the sub-list
        for (int i = 1; i < start; i++) {
            if (pre.next == null) return head; // start index is out of bound
            pre = pre.next;
        }

        ListNode current = pre.next;
        if (current == null) return head; // start index is out of bound

        // Reverse the sub-list using similar logic as the reverseList function
        ListNode next = null, prev = null;
        ListNode subListHead = current;

        for (int i = start; i <= end; i++) {
            if (current == null) break; // end index out of bound
            next = current.next;
            current.next = prev;
            prev = current;
            current = next;
        }

        // Connect the reversed sub-list back to the original list
        pre.next = prev;        // pre.next (before sublist) points to the reversed sublist head (previously the last element)
        subListHead.next = current; // Sublist tail points to the node after the reversed sublist

        return dummy.next; // Return the original head (or new head if head was part of sublist)
    }



    // Helper function to create a linked list from an array
    public static ListNode createLinkedList(int[] arr) {
        if (arr == null || arr.length == 0) {
            return null;
        }
        ListNode head = new ListNode(arr[0]);
        ListNode current = head;
        for (int i = 1; i < arr.length; i++) {
            current.next = new ListNode(arr[i]);
            current = current.next;
        }
        return head;
    }

    // Helper function to print the linked list
    public static void printLinkedList(ListNode head) {
        ListNode current = head;
        while (current != null) {
            System.out.print(current.val + "->");
            current = current.next;
        }
        System.out.println("NULL");
    }

    public static void main(String[] args) {
        // Test case 1: Empty list
        ListNode head1 = null;
        System.out.println("Original List 1:");
        printLinkedList(head1);
        ListNode reversedHead1 = reverseList(head1);
        System.out.println("Reversed List 1:");
        printLinkedList(reversedHead1); // Expected: NULL

        // Test case 2: Single node list
        ListNode head2 = new ListNode(1);
        System.out.println("Original List 2:");
        printLinkedList(head2);
        ListNode reversedHead2 = reverseList(head2);
        System.out.println("Reversed List 2:");
        printLinkedList(reversedHead2); // Expected: 1->NULL

        // Test case 3: Multiple nodes list
        int[] arr3 = {1, 2, 3, 4};
        ListNode head3 = createLinkedList(arr3);
        System.out.println("Original List 3:");
        printLinkedList(head3);
        ListNode reversedHead3 = reverseList(head3);
        System.out.println("Reversed List 3:");
        printLinkedList(reversedHead3); // Expected: 4->3->2->1->NULL

        // Test case 4: Another multiple nodes list
        int[] arr4 = {1, 2, 3, 4, 5};
        ListNode head4 = createLinkedList(arr4);
        System.out.println("Original List 4:");
        printLinkedList(head4);
        ListNode reversedHead4 = reverseList(head4);
        System.out.println("Reversed List 4:");
        printLinkedList(reversedHead4); // Expected: 5->4->3->2->1->NULL

        // Test cases for reverseSubList

        // Test case 5: Reverse a sublist
        int[] arr5 = {1, 2, 3, 4, 5};
        ListNode head5 = createLinkedList(arr5);
        System.out.println("Original List 5:");
        printLinkedList(head5);
        ListNode reversedSubListHead5 = reverseSubList(head5, 2, 4);
        System.out.println("Reversed Sub-list 5 (2-4):");
        printLinkedList(reversedSubListHead5); // Expected: 1->4->3->2->5->NULL

        // Test case 6: Reverse the entire list using reverseSubList
        int[] arr6 = {1, 2, 3, 4, 5};
        ListNode head6 = createLinkedList(arr6);
        System.out.println("Original List 6:");
        printLinkedList(head6);
        ListNode reversedSubListHead6 = reverseSubList(head6, 1, 5);
        System.out.println("Reversed Sub-list 6 (1-5):");
        printLinkedList(reversedSubListHead6); // Expected: 5->4->3->2->1->NULL

        // Test case 7: Reverse an invalid sublist (start > end)
        int[] arr7 = {1, 2, 3, 4, 5};
        ListNode head7 = createLinkedList(arr7);
        System.out.println("Original List 7:");
        printLinkedList(head7);
        ListNode reversedSubListHead7 = reverseSubList(head7, 4, 2);
        System.out.println("Reversed Sub-list 7 (4-2):");
        printLinkedList(reversedSubListHead7); // Expected: 1->2->3->4->5->NULL (no change)

        // Test case 8: Reverse a sublist at the beginning
        int[] arr8 = {1, 2, 3, 4, 5};
        ListNode head8 = createLinkedList(arr8);
        System.out.println("Original List 8:");
        printLinkedList(head8);
        ListNode reversedSubListHead8 = reverseSubList(head8, 1, 3);
        System.out.println("Reversed Sub-list 8 (1-3):");
        printLinkedList(reversedSubListHead8); // Expected: 3->2->1->4->5->NULL

        // Test case 9: Reverse a sublist at the end
        int[] arr9 = {1, 2, 3, 4, 5};
        ListNode head9 = createLinkedList(arr9);
        System.out.println("Original List 9:");
        printLinkedList(head9);
        ListNode reversedSubListHead9 = reverseSubList(head9, 3, 5);
        System.out.println("Reversed Sub-list 9 (3-5):");
