import java.util.Arrays;
import java.util.function.Function;

public class ArrayRearrangement {

    /**
     * Rearranges the array in-place so that each element x is placed at index f(x), where f is a deterministic hash function.
     *
     * @param arr The input array of distinct integers.
     * @param hashFunction The hash function that maps each integer to a unique index between 0 and n-1.
     */
    public static void rearrangeArray(int[] arr, Function<Integer, Integer> hashFunction) {
        int n = arr.length;

        // Iterate through the array, performing swaps to place elements in their correct positions
        for (int i = 0; i < n; i++) {
            while (hashFunction.apply(arr[i]) != i) {
                int correctIndex = hashFunction.apply(arr[i]);

                // Swap the element at the current index with the element at its correct index
                int temp = arr[correctIndex];
                arr[correctIndex] = arr[i];
                arr[i] = temp;
            }
        }
    }

    /**
     * Main method to demonstrate the array rearrangement with various test cases.
     *
     * @param args Command line arguments (not used).
     */
    public static void main(String[] args) {
        // Test Case 1: Example provided in the prompt
        int[] arr1 = {4, 2, 8, 6};
        Function<Integer, Integer> hashFunction1 = x -> x / 2 - 1; // Example hash function: f(x) = x/2 - 1
        rearrangeArray(arr1, hashFunction1);
        System.out.println("Test Case 1: " + Arrays.toString(arr1)); // Expected Output: [2, 4, 6, 8]

        // Test Case 2: Simple case with f(x) = x % n
        int[] arr2 = {1, 2, 3, 4, 5};
        Function<Integer, Integer> hashFunction2 = x -> x % 5;
        rearrangeArray(arr2, hashFunction2);
        System.out.println("Test Case 2: " + Arrays.toString(arr2)); // Expected Output: [1, 2, 3, 4, 5] (already sorted correctly)

        // Test Case 3:  Reverse Sorted array with a more complex hash function
        int[] arr3 = {5, 4, 3, 2, 1};
        Function<Integer, Integer> hashFunction3 = x -> (x * 3 + 1) % 5;
        rearrangeArray(arr3, hashFunction3);
        System.out.println("Test Case 3: " + Arrays.toString(arr3)); // Expected Output: [1, 2, 3, 4, 5]

        // Test Case 4: Array with elements already in sorted order but hash function rearranges them
        int[] arr4 = {10, 20, 30, 40};
        Function<Integer, Integer> hashFunction4 = x -> (x / 10 + 2) % 4; // Shifts all elements right by two positions
        rearrangeArray(arr4, hashFunction4);
        System.out.println("Test Case 4: " + Arrays.toString(arr4)); // Expected Output: [30, 40, 10, 20]

        // Test Case 5: Array with negative integers
        int[] arr5 = {-1, -2, -3, -4};
        Function<Integer, Integer> hashFunction5 = x -> Math.abs(x) - 1;  // Simple hash function based on absolute value
        rearrangeArray(arr5, hashFunction5);
        System.out.println("Test Case 5: " + Arrays.toString(arr5)); // Expected Output: [-4, -3, -2, -1]

        // Test Case 6: Larger array
        int[] arr6 = {11, 22, 33, 44, 55, 66, 77, 88};
        Function<Integer, Integer> hashFunction6 = x -> (x/11 * 2) % 8; //f(x) = (x/11 *2) % 8;
        rearrangeArray(arr6, hashFunction6);
        System.out.println("Test Case 6: " + Arrays.toString(arr6)); // Expected Output: [11, 55, 22, 66, 33, 77, 44, 88]
    }
}

