import java.util.Arrays;

public class MinTripTime {

    /**
     * Calculates the minimum time required to make n trips using the given cabs.
     *
     * @param n              The number of trips required.
     * @param cabTravelTime  An array representing the time each cab takes to make a trip.
     * @return The minimum time required to make n trips.
     */
    public static long minTime(int n, int[] cabTravelTime) {
        // Check for null or empty input
        if (cabTravelTime == null || cabTravelTime.length == 0) {
            return 0; // Or throw an exception, depending on requirements.
        }

        // Binary search to find the minimum time
        long low = 1;
        long high = Arrays.stream(cabTravelTime).max().getAsInt() * (long)n; // Potential maximum time
        long ans = high;

        while (low <= high) {
            long mid = low + (high - low) / 2; // Prevent overflow
            long trips = 0;

            // Calculate how many trips each cab can make within 'mid' time
            for (int time : cabTravelTime) {
                trips += mid / time;
            }

            // If we can make enough trips within 'mid' time, try a lower time
            if (trips >= n) {
                ans = mid;
                high = mid - 1;
            } else {
                // Otherwise, we need more time
                low = mid + 1;
            }
        }

        return ans;
    }

    public static void main(String[] args) {
        // Test cases
        int n1 = 3;
        int[] cabTravelTime1 = {1, 2};
        System.out.println("Minimum time for " + n1 + " trips with cabs " + Arrays.toString(cabTravelTime1) + ": " + minTime(n1, cabTravelTime1)); // Expected: 2

        int n2 = 7;
        int[] cabTravelTime2 = {1, 3, 5, 7, 8};
