            this.id = UUID.randomUUID().toString(); // Unique ID for each task
            this.description = description;
            this.deadline = deadline;
            this.tags = new HashSet<>(tags); // Defensive copy
            this.completed = false;
            this.scheduledDate = scheduledDate;
        }

        public String getId() {
            return id;
        }

        public String getDescription() {
            return description;
        }

        public LocalDate getDeadline() {
            return deadline;
        }

        public Set<String> getTags() {
            return new HashSet<>(tags); // Defensive copy
        }

        public boolean isCompleted() {
            return completed;
        }

        public void setCompleted(boolean completed) {
            this.completed = completed;
        }

        public LocalDate getScheduledDate() {
            return scheduledDate;
        }

        public void setDescription(String description) {
            this.description = description;
        }

        public void setDeadline(LocalDate deadline) {
            this.deadline = deadline;
        }

        public void setTags(Set<String> tags) {
            this.tags = new HashSet<>(tags); // Defensive copy
        }

        public void setScheduledDate(LocalDate scheduledDate) {
            this.scheduledDate = scheduledDate;
        }

        @Override
        public String toString() {
            return "TodoTask{" +
                    "id='" + id + '\'' +
                    ", description='" + description + '\'' +
                    ", deadline=" + deadline +
                    ", tags=" + tags +
                    ", completed=" + completed +
                    ", scheduledDate=" + scheduledDate +
                    '}';
        }
    }

    // Inner class representing an activity log entry
    static class ActivityLog {
        private LocalDateTime timestamp;
        private String activity;

        public ActivityLog(String activity) {
            this.timestamp = LocalDateTime.now();
            this.activity = activity;
        }

        public LocalDateTime getTimestamp() {
            return timestamp;
        }

        public String getActivity() {
            return activity;
        }

        @Override
        public String toString() {
            return "ActivityLog{" +
                    "timestamp=" + timestamp +
                    ", activity='" + activity + '\'' +
                    '}';
        }
    }

    // Adds a new task to the TODO list.
    // Time Complexity: O(1) -  Adding to the end of an ArrayList is typically O(1).
    // Space Complexity: O(1) - Constant space for creating a new TodoTask object.
    public void addTask(String description, LocalDate deadline, Set<String> tags, LocalDate scheduledDate) {
        TodoTask task = new TodoTask(description, deadline, tags, scheduledDate);
        todoList.add(task);
        activityLog.add(new ActivityLog("Added task: " + task.getDescription()));
    }

    // Updates an existing task in the TODO list.
    // Time Complexity: O(n) -  Worst case, we iterate through the entire list to find the task.  Can be improved to O(1) by using a HashMap.
    // Space Complexity: O(1) - Constant space for modifications.
    public boolean updateTask(String taskId, String description, LocalDate deadline, Set<String> tags, LocalDate scheduledDate) {
        for (TodoTask task : todoList) {
            if (task.getId().equals(taskId)) {
                task.setDescription(description);
                task.setDeadline(deadline);
                task.setTags(tags);
                task.setScheduledDate(scheduledDate);
                activityLog.add(new ActivityLog("Updated task: " + task.getDescription()));
                return true;
            }
        }
        return false;
    }

    // Removes a task from the TODO list.
    // Time Complexity: O(n) - Worst case, we iterate through the entire list to find the task. Can be improved to O(1) by using a HashMap.
    // Space Complexity: O(1) - Constant space for modifications.
    public boolean removeTask(String taskId) {
        for (TodoTask task : todoList) {
            if (task.getId().equals(taskId)) {
                todoList.remove(task);
                activityLog.add(new ActivityLog("Removed task: " + task.getDescription()));
                return true;
            }
        }
        return false;
    }

    // Marks a task as completed and removes it from the list.
    // Time Complexity: O(n) - Worst case, we iterate through the entire list to find the task. Can be improved to O(1) by using a HashMap.
    // Space Complexity: O(1) - Constant space for modifications.
    public boolean completeTask(String taskId) {
        for (TodoTask task : todoList) {
            if (task.getId().equals(taskId)) {
                task.setCompleted(true);
                todoList.remove(task);
                activityLog.add(new ActivityLog("Completed task: " + task.getDescription()));
                return true;
            }
        }
        return false;
    }

    // Retrieves the current TODO list, filtering out tasks that are scheduled for a future date.
    // Time Complexity: O(n) - Iterates through the todoList.
    // Space Complexity: O(n) -  Creates a new list to store filtered tasks (worst-case, all tasks are included).
    public List<TodoTask> getTodoList() {
       return todoList.stream()
               .filter(task -> task.getScheduledDate().isBefore(LocalDate.now().plusDays(1))) //Include scheduled tasks if their scheduled date is today or earlier.
               .collect(Collectors.toList());
    }


    // Retrieves the activity log for a given time period.
    // Time Complexity: O(n) - Iterates through the activityLog.
    // Space Complexity: O(n) -  Creates a new list to store filtered logs (worst-case, all logs are included).
    public List<ActivityLog> getActivityLog(LocalDateTime startTime, LocalDateTime endTime) {
        return activityLog.stream()
                .filter(log -> log.getTimestamp().isAfter(startTime) && log.getTimestamp().isBefore(endTime))
                .collect(Collectors.toList());
    }

    // Calculates statistics about tasks added, completed, and overdue within a given time period.
    // Time Complexity: O(n) -  Iterates through the todoList and activityLog.
    // Space Complexity: O(1) - Constant space for storing counters.
    public Map<String, Long> getTaskStatistics(LocalDateTime startTime, LocalDateTime endTime) {
        long addedTasks = activityLog.stream()
                .filter(log -> log.getTimestamp().isAfter(startTime) && log.getTimestamp().isBefore(endTime) && log.getActivity().startsWith("Added"))
                .count();

        long completedTasks = activityLog.stream()
                .filter(log -> log.getTimestamp().isAfter(startTime) && log.getTimestamp().isBefore(endTime) && log.getActivity().startsWith("Completed"))
                .count();

        long overdueTasks = todoList.stream()
                .filter(task -> task.getDeadline() != null && task.getDeadline().isBefore(LocalDate.now()))
                .count();

        Map<String, Long> statistics = new HashMap<>();
        statistics.put("added", addedTasks);
        statistics.put("completed", completedTasks);
        statistics.put("overdue", overdueTasks);
        return statistics;
    }

