import java.util.LinkedList;
import java.util.Queue;

public class BinaryTreeBoxes {

    // Node class for the binary tree
    static class Node {
        int data;
        Node left, right;

        Node(int data) {
            this.data = data;
            left = right = null;
        }
    }

    /**
     * Calculates the minimum number of moves required to distribute boxes such that each node has exactly one box.
     *
     * @param root The root of the binary tree.
     * @return The minimum number of moves required.
     */
    public static int distributeBoxes(Node root) {
        int[] moves = new int[1]; // Using an array to pass by reference
        distributeBoxesHelper(root, moves);
        return moves[0];
    }

    /**
     * Recursive helper function to traverse the tree and calculate the moves.
     *
     * @param node  The current node being processed.
     * @param moves An array to store the total moves (passed by reference).
     * @return The excess or deficit of boxes at the current node.  Positive means excess, negative means deficit.
     */
    private static int distributeBoxesHelper(Node node, int[] moves) {
        if (node == null) {
            return 0;
        }

        int leftExcess = distributeBoxesHelper(node.left, moves);
        int rightExcess = distributeBoxesHelper(node.right, moves);

        moves[0] += Math.abs(leftExcess) + Math.abs(rightExcess); // Accumulate moves

        return node.data + leftExcess + rightExcess - 1; // Calculate excess/deficit for the current node
    }


    public static void main(String[] args) {
        // Test cases
        Node root1 = new Node(3);
        root1.left = new Node(0);
        root1.right = new Node(0);
        System.out.println("Example 1: " + distributeBoxes(root1)); // Output: 2

        Node root2 = new Node(0);
        root2.left = new Node(3);
        root2.right = new Node(0);
        System.out.println("Example 2: " + distributeBoxes(root2)); // Output: 3

        Node root3 = new Node(1);
        root3.left = new Node(0);
        root3.right = new Node(0);
        System.out.println("Example 3: " + distributeBoxes(root3)); // Output: 0

        Node root4 = new Node(0);
        root4.left = new Node(0);
        root4.right = new Node(4);
        System.out.println("Example 4: " + distributeBoxes(root4)); // Output: 3

        Node root5 = new Node(1);
        root5.left = new Node(2);
        root5.right = new Node(3);
        root5.left.left = new Node(4);
        root5.left.right = new Node(5);

        System.out.println("Example 5: " + distributeBoxes(root5)); //output 8

        Node root6 = new Node(1);
        System.out.println("Example 6: " + distributeBoxes(root6)); // Output 0

        Node root7 = new Node(0);
        root7.left = new Node(0);
        root7.right = new Node(0);
        System.out.println("Example 7: " + distributeBoxes(root7)); // Output 2
    }

    /*
     * Time Complexity: O(N), where N is the number of nodes in the binary tree.
     * We visit each node exactly once during the depth-first traversal.
     *
     * Space Complexity: O(H), where H is the height of the binary tree.
     * In the worst case (skewed tree), H = N, resulting in O(N) space complexity.
     * In the best case (balanced tree), H = log N, resulting in O(log N) space complexity.
     * The space complexity comes from the recursive call stack.
     */
}
