import java.util.Arrays;

/**
 * Banker's Algorithm Implementation in Java
 *
 * The Banker's Algorithm is a resource allocation and deadlock avoidance algorithm
 * developed by Edsger W. Dijkstra. It tests for safety by simulating the allocation
 * of predetermined maximum possible amounts of all resources, then makes an "s-state"
 * check to test for possible deadlock conditions for all activities, before deciding
 * whether allocation should be allowed to continue.
 *
 * This implementation includes the following:
 * - Resource allocation checking
 * - Safety Algorithm to determine if the system is in a safe state
 * - Example usage with test cases, including edge cases.
 *
 * Time Complexity: O(n^2 * m) where n is the number of processes and m is the number of resources
 * Space Complexity: O(n * m)
 */
public class BankersAlgorithm {

    private int[] available;     // Available resources
    private int[][] max;         // Maximum resource needs of each process
    private int[][] allocation;    // Resources currently allocated to each process
    private int[][] need;          // Remaining resource needs of each process (max - allocation)
    private int numProcesses;
    private int numResources;

    /**
     * Constructor for the BankersAlgorithm class.
     *
     * @param available  An array representing the available resources.
     * @param max        A 2D array representing the maximum resource needs of each process.
     * @param allocation A 2D array representing the resources currently allocated to each process.
     */
    public BankersAlgorithm(int[] available, int[][] max, int[][] allocation) {
        this.available = Arrays.copyOf(available, available.length); // Defensive copy
        this.max = deepCopy(max); // Defensive copy
        this.allocation = deepCopy(allocation); // Defensive copy
        this.numProcesses = max.length;
        this.numResources = available.length;
        this.need = calculateNeed(max, allocation);
    }


    //Helper method to deep copy a 2D array
    private int[][] deepCopy(int[][] original) {
        int[][] copy = new int[original.length][];
        for (int i = 0; i < original.length; i++) {
            copy[i] = Arrays.copyOf(original[i], original[i].length);
        }
        return copy;
    }

    /**
     * Calculates the remaining resource needs (Need) for each process.
     *
     * @param max        A 2D array representing the maximum resource needs.
     * @param allocation A 2D array representing the resources currently allocated.
     * @return A 2D array representing the remaining resource needs.
     */
    private int[][] calculateNeed(int[][] max, int[][] allocation) {
        int[][] need = new int[numProcesses][numResources];
        for (int i = 0; i < numProcesses; i++) {
            for (int j = 0; j < numResources; j++) {
                need[i][j] = max[i][j] - allocation[i][j];
            }
        }
        return need;
    }

    /**
     * Checks if a request for resources can be granted safely.
     *
     * @param processId The ID of the process requesting resources.
     * @param request   An array representing the resource request.
     * @return True if the request can be granted safely, false otherwise.
     */
    public synchronized boolean isRequestSafe(int processId, int[] request) {

        // Step 1: Check if the request exceeds the need.
        for (int i = 0; i < numResources; i++) {
            if (request[i] > need[processId][i]) {
                System.out.println("Process " + processId + " exceeded its maximum claim.");
                return false;
            }
        }

        // Step 2: Check if the request exceeds available resources.
        for (int i = 0; i < numResources; i++) {
            if (request[i] > available[i]) {
                System.out.println("Not enough resources available.");
                return false;
            }
        }

        // Step 3: Simulate the allocation.
        int[] tempAvailable = Arrays.copyOf(available, available.length); // Create a copy
        int[][] tempAllocation = deepCopy(allocation);
        int[][] tempNeed = deepCopy(need);

        for (int i = 0; i < numResources; i++) {
            tempAvailable[i] -= request[i];
            tempAllocation[processId][i] += request[i];
            tempNeed[processId][i] -= request[i];
        }

        // Step 4: Check if the system is in a safe state.
        if (!isSafeState(tempAvailable, tempAllocation, tempNeed)) {
            System.out.println("Request would lead to an unsafe state.");
            return false;
        }

        System.out.println("Request can be granted safely.");
        return true;
    }


    /**
     * Executes the resource request if it is safe.  This will update the state of the system.
     *
     * @param processId The ID of the process requesting resources.
     * @param request   An array representing the resource request.
     * @return True if the request was successfully granted, false otherwise.
     */
    public synchronized boolean requestResources(int processId, int[] request) {
        if (isRequestSafe(processId, request)) {
            // Grant the request (update state)
            for (int i = 0; i < numResources; i++) {
                available[i] -= request[i];
                allocation[processId][i] += request[i];
                need[processId][i] -= request[i];
            }
            return true;
        }
        return false;
    }

    /**
     * Releases resources held by a process.  Updates the state of the system.
     *
     * @param processId The ID of the process releasing resources.
     * @param release   An array representing the resources being released.
     */
    public synchronized void releaseResources(int processId, int[] release) {
        for (int i = 0; i < numResources; i++) {
            available[i] += release[i];
            allocation[processId][i] -= release[i];
            need[processId][i] += release[i];
        }
    }


    /**
     * Checks if the system is in a safe state.
     *
     * @param tempAvailable  A copy of the available resources array for simulation.
     * @param tempAllocation A copy of the allocation array for simulation.
     * @param tempNeed       A copy of the need array for simulation.
     * @return True if the system is in a safe state, false otherwise.
     */
    private boolean isSafeState(int[] tempAvailable, int[][] tempAllocation, int[][] tempNeed) {
        boolean[] finished = new boolean[numProcesses];
        int[] work = Arrays.copyOf(tempAvailable, tempAvailable.length);
        int count = 0;

        while (count < numProcesses) {
            boolean found = false;
            for (int i = 0; i < numProcesses; i++) {
                if (!finished[i]) {
                    int j;
                    for (j = 0; j < numResources; j++) {
                        if (tempNeed[i][j] > work[j])
                            break;
                    }
                    if (j == numResources) {
                        for (int k = 0; k < numResources; k++) {
                            work[k] += tempAllocation[i][k];
                        }
                        finished[i] = true;
                        found = true;
                        count++;
                    }
                }
            }
            if (!found) {
                return false; // System is in an unsafe state
            }
        }
        return true; // System is in a safe state
    }


    /**
     * Main method for testing the Banker's Algorithm.
     *
     * @param args Command line arguments (not used).
     */
    public static void main(String[] args) {
        // Example 1
        int[] available1 = {3, 3, 2};
