import java.util.*;

public class ConcatenatedWords {

    /**
     * Finds all concatenated words in a given array of unique strings.
     * A concatenated word is a string that can be formed by combining at least two shorter words from the same array.
     *
     * @param words An array of unique strings.
     * @return A list of concatenated words.
     *
     * Time Complexity: O(N * L^2), where N is the number of words and L is the average length of a word. The
     * sorting takes O(N log N) but is dominated by the nested loops in the `canForm` method.
     *
     * Space Complexity: O(N), primarily due to the `wordSet` and the potential size of the `result` list.
     */
    public List<String> findAllConcatenatedWords(String[] words) {
        List<String> result = new ArrayList<>();
        Set<String> wordSet = new HashSet<>(Arrays.asList(words));

        // Sort the words by length in ascending order. This is an optimization that allows
        // us to check shorter words first, which may be used to form longer words.
        Arrays.sort(words, Comparator.comparingInt(String::length));

        for (String word : words) {
            // Remove the current word from the set to avoid using it to form itself.
            wordSet.remove(word);
            if (canForm(word, wordSet)) {
                result.add(word);
            }
            // Add the word back to the set for future checks.
            wordSet.add(word);
        }

        return result;
    }

    /**
     * Checks if a given word can be formed by concatenating shorter words from the provided set.
     *
     * @param word    The word to check.
     * @param wordSet The set of words to use for concatenation.
     * @return True if the word can be formed, false otherwise.
     *
     * Time Complexity: O(L^2), where L is the length of the word. The outer loop iterates up to L times, and
     * the `substring` operations inside the loop take O(L) time.
     *
     * Space Complexity: O(L) in the worst-case scenario due to the recursive calls on the `dp` array.
     */
    private boolean canForm(String word, Set<String> wordSet) {
        if (word.isEmpty()) {
            return false; // Empty string cannot be formed by concatenation.
        }

        boolean[] dp = new boolean[word.length() + 1]; // dp[i] is true if word.substring(0, i) can be formed
        dp[0] = true; // Empty string can be formed

        for (int i = 1; i <= word.length(); i++) {
            for (int j = 0; j < i; j++) {
                if (dp[j] && wordSet.contains(word.substring(j, i))) {
                    dp[i] = true;
                    break; // Optimization: If we found a way to form the prefix, no need to continue
                }
            }
        }

        return dp[word.length()];
    }


    public static void main(String[] args) {
        ConcatenatedWords sol = new ConcatenatedWords();

        // Test case 1
        String[] words1 = {"bogie", "engine", "enginebogie"};
        List<String> result1 = sol.findAllConcatenatedWords(words1);
        System.out.println("Test Case 1: " + result1); // Expected: [enginebogie]

        // Test case 2
        String[] words2 = {"cat", "dog", "catdog"};
        List<String> result2 = sol.findAllConcatenatedWords(words2);
        System.out.println("Test Case 2: " + result2); // Expected: [catdog]

        // Test case 3
        String[] words3 = {"cat", "cats", "catsdogcats", "dog", "dogcatsdog", "hippopotamus", "rat"};
        List<String> result3 = sol.findAllConcatenatedWords(words3);
        System.out.println("Test Case 3: " + result3); // Expected: [catsdogcats, dogcatsdog]

        // Test case 4 - Empty input
        String[] words4 = {};
        List<String> result4 = sol.findAllConcatenatedWords(words4);
        System.out.println("Test Case 4 (Empty Input): " + result4); // Expected: []

        // Test case 5 - No concatenated words
        String[] words5 = {"cat", "dog", "mouse"};
        List<String> result5 = sol.findAllConcatenatedWords(words5);
        System.out.println("Test Case 5 (No Concatenated Words): " + result5); // Expected: []

        // Test case 6 - Single word input
        String[] words6 = {"cat"};
        List<String> result6 = sol.findAllConcatenatedWords(words6);
        System.out.println("Test Case 6 (Single Word Input): " + result6); // Expected: []

        // Test case 7 - Larger example
        String[] words7 = {"a", "b", "c", "ab", "bc", "abc"};
        List<String> result7 = sol.findAllConcatenatedWords(words7);
        System.out.println("Test Case 7 (Larger Example): " + result7); // Expected: [ab, bc, abc]

        // Test case 8 - Overlapping example
        String[] words8 = {"a", "aa", "aaa", "aaaa"};
        List<String> result8 = sol.findAllConcatenatedWords(words8);
        System.out.println("Test Case 8 (Overlapping): " + result8); // Expected: [aa, aaa, aaaa]

        // Test case 9 - Long concatenated word
        String[] words9 = {"ab", "cd", "abcdabcd"};
        List<String> result9 = sol.findAllConcatenatedWords(words9);
        System.out.println("Test Case 9 (Long concatenated word): " + result9); // Expected: [abcdabcd]

    }
}
