import java.util.HashSet;
import java.util.Set;

public class RemoveAdjacentDuplicates {

    /**
     * Removes adjacent duplicate nodes from a sorted linked list.
     *
     * @param head The head of the sorted linked list.
     * @return The head of the modified linked list with adjacent duplicates removed.
     *         Returns null if the input list is null or empty.
     */
    public static ListNode deleteAdjacentDuplicates(ListNode head) {
        if (head == null || head.next == null) {
            return head; // Nothing to do if the list is empty or has only one element.
        }

        ListNode dummy = new ListNode(-1); // Dummy node to simplify handling the head.
        dummy.next = head;
        ListNode prev = dummy;
        ListNode curr = head;

        while (curr != null) {
            if (curr.next != null && curr.val == curr.next.val) {
                // Found adjacent duplicates
                int duplicateValue = curr.val;
                // Skip all nodes with the same value
                while (curr != null && curr.val == duplicateValue) {
                    curr = curr.next;
                }
                // Update the previous node to point to the node after the duplicates
                prev.next = curr;
            } else {
                // No adjacent duplicates, move both pointers forward
                prev = curr;
                curr = curr.next;
            }
        }

        return dummy.next; // Return the head of the modified list.
    }


    /**
     * ListNode class representing a node in the linked list.
     */
    public static class ListNode {
        int val;
        ListNode next;

        ListNode() {}
        ListNode(int val) { this.val = val; }
        ListNode(int val, ListNode next) { this.val = val; this.next = next; }

        @Override
        public String toString() {
            return String.valueOf(val);
        }
    }

    public static void main(String[] args) {
        // Test cases

        // Test case 1: Example from the prompt
        ListNode head1 = new ListNode(1, new ListNode(2, new ListNode(2, new ListNode(3))));
        ListNode result1 = deleteAdjacentDuplicates(head1);
        printList(result1); // Expected: 1 -> 3

        // Test case 2: List with all duplicates
        ListNode head2 = new ListNode(1, new ListNode(1, new ListNode(1)));
        ListNode result2 = deleteAdjacentDuplicates(head2);
        printList(result2); // Expected: null

        // Test case 3: List with no duplicates
        ListNode head3 = new ListNode(1, new ListNode(2, new ListNode(3, new ListNode(4))));
        ListNode result3 = deleteAdjacentDuplicates(head3);
        printList(result3); // Expected: 1 -> 2 -> 3 -> 4

        // Test case 4: Empty list
        ListNode head4 = null;
        ListNode result4 = deleteAdjacentDuplicates(head4);
        printList(result4); // Expected: null

        // Test case 5: List with duplicates at the beginning
        ListNode head5 = new ListNode(1, new ListNode(1, new ListNode(2, new ListNode(3))));
        ListNode result5 = deleteAdjacentDuplicates(head5);
        printList(result5); // Expected: 2 -> 3

        // Test case 6: List with duplicates at the end
        ListNode head6 = new ListNode(1, new ListNode(2, new ListNode(3, new ListNode(3))));
        ListNode result6 = deleteAdjacentDuplicates(head6);
        printList(result6); // Expected: 1 -> 2

        // Test case 7: List with multiple duplicate groups
        ListNode head7 = new ListNode(1, new ListNode(2, new ListNode(2, new ListNode(3, new ListNode(3, new ListNode(4)))));
        ListNode result7 = deleteAdjacentDuplicates(head7);
        printList(result7); // Expected: 1 -> 4

        // Test case 8: Single element list
        ListNode head8 = new ListNode(1);
        ListNode result8 = deleteAdjacentDuplicates(head8);
        printList(result8); // Expected: 1

        // Test case 9: Longer list with interspersed duplicates
        ListNode head9 = new ListNode(1, new ListNode(2, new ListNode(2, new ListNode(3, new ListNode(4, new ListNode(4, new ListNode(5, new ListNode(6, new ListNode(6))))))));
        ListNode result9 = deleteAdjacentDuplicates(head9);
        printList(result9); // Expected: 1 -> 3 -> 5

        // Test case 10: All same value (longer)
        ListNode head10 = new ListNode(5, new ListNode(5, new ListNode(5, new ListNode(5, new ListNode(5)))));
        ListNode result10 = deleteAdjacentDuplicates(head10);
        printList(result10); // Expected: null

    }

    /**
     * Helper method to print the linked list.
     *
     * @param head The head of the linked list.
     */
    private static void printList(ListNode head) {
        ListNode current = head;
        while (current != null) {
            System.out.print(current.val + " -> ");
            current = current.next;
        }
        System.out.println("null");
    }
}

