            this.productId = productId;
            this.name = name;
            this.price = price;
            this.availableStock = availableStock;
        }
    }

    /**
     * Represents a subscription of a user to a product.
     */
    static class Subscription {
        int subscriptionId;
        int userId;
        int productId;
        String frequency; // DAILY, WEEKLY, MONTHLY
        LocalDate startDate;
        LocalTime deliveryTime; // Time of day for delivery
        boolean isActive;

        public Subscription(int subscriptionId, int userId, int productId, String frequency, LocalDate startDate, LocalTime deliveryTime, boolean isActive) {
            this.subscriptionId = subscriptionId;
            this.userId = userId;
            this.productId = productId;
            this.frequency = frequency;
            this.startDate = startDate;
            this.deliveryTime = deliveryTime;
            this.isActive = isActive;
        }
    }

    /**
     * Represents a scheduled order.
     */
    static class Order {
        int orderId;
        int subscriptionId;
        int userId;
        int productId;
        LocalDate orderDate;
        double amount;
        String status; // PENDING, PROCESSING, SHIPPED, DELIVERED, FAILED
        String deliveryAddress;

        public Order(int orderId, int subscriptionId, int userId, int productId, LocalDate orderDate, double amount, String status, String deliveryAddress) {
            this.orderId = orderId;
            this.subscriptionId = subscriptionId;
            this.userId = userId;
            this.productId = productId;
            this.orderDate = orderDate;
            this.amount = amount;
            this.status = status;
            this.deliveryAddress = deliveryAddress;
        }
    }

    /**
     * Represents a payment method.
     */
    static class PaymentMethod {
        String type; // CREDIT_CARD, PAYPAL, etc.
        String details; // Encrypted details

        public PaymentMethod(String type, String details) {
            this.type = type;
            this.details = details;
        }
    }

    // Data structures to store data (In a real system, these would be databases)
    private final Map<Integer, User> users = new ConcurrentHashMap<>();
    private final Map<Integer, Product> products = new ConcurrentHashMap<>();
    private final Map<Integer, Subscription> subscriptions = new ConcurrentHashMap<>();
    private final Map<Integer, Order> orders = new ConcurrentHashMap<>();

    private final AtomicInteger orderIdCounter = new AtomicInteger(1);
    private final AtomicInteger subscriptionIdCounter = new AtomicInteger(1);

    // Executor Service for handling asynchronous tasks
    private final ExecutorService executorService = Executors.newFixedThreadPool(10);  // Adjust pool size based on expected load

    // Retry policy configurations
    private static final int MAX_PAYMENT_RETRIES = 3;
    private static final long RETRY_DELAY_MS = 1000; // 1 second

    //-------------------------- Core Functionality --------------------------

    /**
     * Subscribes a user to a product.
     * @param userId The ID of the user.
     * @param productId The ID of the product.
     * @param frequency The subscription frequency (DAILY, WEEKLY, MONTHLY).
     * @param startDate The start date of the subscription.
     * @param deliveryTime The preferred delivery time.
     * @return The ID of the newly created subscription.
     */
    public int subscribeUserToProduct(int userId, int productId, String frequency, LocalDate startDate, LocalTime deliveryTime) {
        if (!users.containsKey(userId) || !products.containsKey(productId)) {
            throw new IllegalArgumentException("Invalid user or product ID.");
        }

        int subscriptionId = subscriptionIdCounter.getAndIncrement();
        Subscription newSubscription = new Subscription(subscriptionId, userId, productId, frequency, startDate, deliveryTime, true);
        subscriptions.put(subscriptionId, newSubscription);
        return subscriptionId;
    }

    /**
     * Pauses a subscription.
     * @param subscriptionId The ID of the subscription to pause.
     */
    public void pauseSubscription(int subscriptionId) {
        Subscription subscription = subscriptions.get(subscriptionId);
        if (subscription != null) {
            subscription.isActive = false;
        }
    }

    /**
     * Resumes a paused subscription.
     * @param subscriptionId The ID of the subscription to resume.
     */
    public void resumeSubscription(int subscriptionId) {
        Subscription subscription = subscriptions.get(subscriptionId);
        if (subscription != null) {
            subscription.isActive = true;
        }
    }

