    /**
     * Schedules orders for all active subscriptions for a given date. This method can be run daily.
     * @param date The date for which to schedule orders.
     */
    public void scheduleOrdersForDate(LocalDate date) {
        subscriptions.values().stream()
                .filter(subscription -> subscription.isActive)
                .filter(subscription -> shouldCreateOrder(subscription, date))
                .forEach(subscription -> executorService.submit(() -> createOrder(subscription, date))); // Asynchronous order creation
    }

    /**
     * Checks if an order should be created for a subscription on a given date.
     * @param subscription The subscription to check.
     * @param date The date to check.
     * @return True if an order should be created, false otherwise.
     */
    private boolean shouldCreateOrder(Subscription subscription, LocalDate date) {
        switch (subscription.frequency) {
            case DAILY:
                return true;
            case WEEKLY:
                return subscription.startDate.getDayOfWeek() == date.getDayOfWeek();
            case MONTHLY:
                return subscription.startDate.getDayOfMonth() == date.getDayOfMonth();
            default:
                return false;
        }
    }

    /**
