     */
    private void createOrder(Subscription subscription, LocalDate orderDate) {
        Product product = products.get(subscription.productId);
        User user = users.get(subscription.userId);

        if (product == null || user == null) {
            System.err.println("Product or user not found for subscription: " + subscription.subscriptionId);
            return; // Handle the case where the product or user is no longer valid.
        }

        // Check inventory
        if (product.availableStock <= 0) {
            notifyUserOfInventoryFailure(user, product);
            return;
