        // Atomically decrement stock
        synchronized (product) {
            if (product.availableStock > 0) {
                product.availableStock--;
            } else {
                notifyUserOfInventoryFailure(user, product);
                return;
            }
        }

        int orderId = orderIdCounter.getAndIncrement();
        Order order = new Order(orderId, subscription.subscriptionId, subscription.userId, subscription.productId,
                orderDate, product.price, "PENDING", user.deliveryAddress);
        orders.put(orderId, order);

        processPayment(order, user);

