import java.util.ArrayList;
import java.util.LinkedList;
import java.util.List;
import java.util.Queue;

public class CourseSchedule {

    /**
     * Determines if it is possible to finish all courses given the prerequisites.
     *
     * @param numCourses    The number of courses.
     * @param prerequisites A 2D array where prerequisites[i] = [ai, bi] means course ai depends on course bi.
     * @return True if it is possible to finish all courses, false otherwise.
     *
     * Time Complexity: O(V + E), where V is the number of courses (vertices) and E is the number of prerequisites (edges).
     * Space Complexity: O(V + E), for the adjacency list and the in-degree array.
     */
    public boolean canFinish(int numCourses, int[][] prerequisites) {
        // 1. Build the graph (adjacency list) and in-degree array.
        List<List<Integer>> adj = new ArrayList<>();
        int[] inDegree = new int[numCourses];

        for (int i = 0; i < numCourses; i++) {
            adj.add(new ArrayList<>());
        }

        for (int[] prerequisite : prerequisites) {
            int course = prerequisite[0];
            int pre = prerequisite[1];
            adj.get(pre).add(course);
            inDegree[course]++;
        }

        // 2. Add all nodes with in-degree 0 to the queue.
        Queue<Integer> queue = new LinkedList<>();
        for (int i = 0; i < numCourses; i++) {
            if (inDegree[i] == 0) {
                queue.offer(i);
            }
        }

        // 3. Perform topological sort.
        int count = 0;
        while (!queue.isEmpty()) {
            int course = queue.poll();
            count++;

            for (int neighbor : adj.get(course)) {
                inDegree[neighbor]--;
                if (inDegree[neighbor] == 0) {
                    queue.offer(neighbor);
                }
            }
        }

        // 4. Check if all courses were visited.
        return count == numCourses;
    }

    public static void main(String[] args) {
        CourseSchedule courseSchedule = new CourseSchedule();

        // Test case 1
        int numCourses1 = 2;
        int[][] prerequisites1 = {{1, 0}};
        System.out.println("Test Case 1: " + courseSchedule.canFinish(numCourses1, prerequisites1)); // Expected: true

        // Test case 2
        int numCourses2 = 2;
        int[][] prerequisites2 = {{1, 0}, {0, 1}};
        System.out.println("Test Case 2: " + courseSchedule.canFinish(numCourses2, prerequisites2)); // Expected: false

        // Test case 3
        int numCourses3 = 3;
        int[][] prerequisites3 = {{0, 1}, {0, 2}, {1, 2}};
        System.out.println("Test Case 3: " + courseSchedule.canFinish(numCourses3, prerequisites3)); // Expected: true

        // Test case 4: Empty prerequisites
        int numCourses4 = 4;
        int[][] prerequisites4 = {};
        System.out.println("Test Case 4: " + courseSchedule.canFinish(numCourses4, prerequisites4)); // Expected: true

        // Test case 5: Single course, no prerequisites
        int numCourses5 = 1;
        int[][] prerequisites5 = {};
        System.out.println("Test Case 5: " + courseSchedule.canFinish(numCourses5, prerequisites5)); // Expected: true

        // Test case 6: More complex graph
        int numCourses6 = 5;
        int[][] prerequisites6 = {{0, 1}, {0, 2}, {1, 3}, {1, 4}, {3, 2}, {4, 2}};
        System.out.println("Test Case 6: " + courseSchedule.canFinish(numCourses6, prerequisites6)); // Expected: true

        // Test case 7: Cycle in more complex graph
        int numCourses7 = 4;
        int[][] prerequisites7 = {{0, 1}, {1, 2}, {2, 3}, {3, 0}};
        System.out.println("Test Case 7: " + courseSchedule.canFinish(numCourses7, prerequisites7)); // Expected: false
    }
}
