import java.util.*;

public class EvaluateDivision {

    /**
     * Evaluates the division result for given equations, values, and queries.
     *
     * @param equations A list of equation pairs as strings.
     * @param values    A list of corresponding values for the equations.
     * @param queries   A list of query pairs as strings.
     * @return An array of doubles representing the results of the queries. Returns -1.0
     *         if a result cannot be found.
     *
     * Time Complexity: O(Q * (V + E)), where Q is the number of queries, V is the
     * number of variables, and E is the number of equations.  In worst case, DFS
     * might visit all vertices and edges for each query.
     * Space Complexity: O(V + E), where V is the number of variables (vertices)
     * and E is the number of equations (edges).  This accounts for the adjacency
     * list (graph) and the visited set used in DFS.
     */
    public double[] calcEquation(List<List<String>> equations, double[] values, List<List<String>> queries) {
        // Build the graph (adjacency list)
        Map<String, Map<String, Double>> graph = buildGraph(equations, values);

        double[] results = new double[queries.size()];
        for (int i = 0; i < queries.size(); i++) {
            String start = queries.get(i).get(0);
            String end = queries.get(i).get(1);
            results[i] = solveQuery(graph, start, end, new HashSet<>());
        }
        return results;
    }

    /**
     * Builds the graph (adjacency list) from the equations and values.
     *
     * @param equations A list of equation pairs.
     * @param values    An array of corresponding values.
     * @return A map representing the graph, where keys are variables and values are
     *         maps of adjacent variables and their corresponding division values.
     */
    private Map<String, Map<String, Double>> buildGraph(List<List<String>> equations, double[] values) {
        Map<String, Map<String, Double>> graph = new HashMap<>();

        for (int i = 0; i < equations.size(); i++) {
            String a = equations.get(i).get(0);
            String b = equations.get(i).get(1);
            double value = values[i];

            // Add edges for a/b and b/a
            graph.computeIfAbsent(a, k -> new HashMap<>()).put(b, value);
            graph.computeIfAbsent(b, k -> new HashMap<>()).put(a, 1.0 / value);
        }

        return graph;
    }

    /**
     * Solves a single query using Depth-First Search (DFS).
     *
     * @param graph     The graph representing the equations.
     * @param start     The starting variable for the query.
     * @param end       The ending variable for the query.
     * @param visited   A set to keep track of visited variables during the search.
     * @return The result of the query, or -1.0 if no path is found.
     */
    private double solveQuery(Map<String, Map<String, Double>> graph, String start, String end, Set<String> visited) {
        // Base cases:
        if (!graph.containsKey(start) || !graph.containsKey(end)) {
            return -1.0; // One or both variables not in the graph
        }

        if (start.equals(end)) {
            return 1.0; // a/a = 1
        }

        visited.add(start);

        // DFS search
        Map<String, Double> neighbors = graph.get(start);
        if (neighbors == null) {
            return -1.0;
        }

        for (Map.Entry<String, Double> entry : neighbors.entrySet()) {
            String neighbor = entry.getKey();
            double value = entry.getValue();

            if (!visited.contains(neighbor)) {
                double result = solveQuery(graph, neighbor, end, visited);
                if (result != -1.0) {
                    return value * result; // a/b * b/c = a/c
                }
            }
        }

        visited.remove(start); // Backtrack: Remove start from visited set
        return -1.0; // No path found
    }

    public static void main(String[] args) {
        EvaluateDivision evaluator = new EvaluateDivision();

        // Test case 1
        List<List<String>> equations1 = Arrays.asList(
                Arrays.asList("a", "b"),
                Arrays.asList("b", "c")
        );
        double[] values1 = {2.0, 3.0};
        List<List<String>> queries1 = Arrays.asList(
                Arrays.asList("a", "c"),
                Arrays.asList("b", "a"),
                Arrays.asList("a", "e"),
                Arrays.asList("a", "a"),
                Arrays.asList("x", "x")
        );
        double[] results1 = evaluator.calcEquation(equations1, values1, queries1);
        System.out.println("Test Case 1: " + Arrays.toString(results1)); // Expected: [6.0, 0.5, -1.0, 1.0, -1.0]

        // Test case 2
        List<List<String>> equations2 = Arrays.asList(
                Arrays.asList("a", "b"),
                Arrays.asList("b", "c"),
                Arrays.asList("bc", "cd")
        );
        double[] values2 = {1.5, 2.5, 5.0};
        List<List<String>> queries2 = Arrays.asList(
                Arrays.asList("a", "c"),
                Arrays.asList("c", "b"),
                Arrays.asList("bc", "cd"),
                Arrays.asList("cd", "bc")
        );
        double[] results2 = evaluator.calcEquation(equations2, values2, queries2);
        System.out.println("Test Case 2: " + Arrays.toString(results2)); // Expected: [3.75, 0.4, 5.0, 0.2]

        // Test case 3 (Edge case: empty equations)
        List<List<String>> equations3 = new ArrayList<>();
        double[] values3 = {};
        List<List<String>> queries3 = Arrays.asList(Arrays.asList("a", "b"));
