import java.util.Stack;

public class ReverseStackRecursion {

    /**
     * Reverses a stack using recursion without extra data structures or loops.
     *
     * @param stack The stack to be reversed.
     */
    public static void reverseStack(Stack<Integer> stack) {
        if (stack.isEmpty()) {
            return; // Base case: empty stack is already reversed.
        }

        int top = stack.pop(); // Remove the top element.
        reverseStack(stack);    // Recursively reverse the remaining stack.
        insertAtBottom(stack, top); // Insert the removed element at the bottom.
    }

    /**
     * Inserts an element at the bottom of the stack using recursion.
     *
     * @param stack The stack to insert into.
     * @param item  The element to insert at the bottom.
     */
    private static void insertAtBottom(Stack<Integer> stack, int item) {
        if (stack.isEmpty()) {
            stack.push(item); // Base case: if the stack is empty, simply push the item.
            return;
        }

        int top = stack.pop(); // Remove the top element.
        insertAtBottom(stack, item); // Recursively insert the item at the bottom.
        stack.push(top); // Push the removed top element back onto the stack.
    }


    public static void main(String[] args) {
        // Test Cases
        System.out.println("Test Cases:");

        // Test Case 1: Empty Stack
        Stack<Integer> stack1 = new Stack<>();
        reverseStack(stack1);
        System.out.print("Empty Stack: ");
        printStack(stack1);  // Expected: []
        System.out.println();

        // Test Case 2: Single Element Stack
        Stack<Integer> stack2 = new Stack<>();
        stack2.push(1);
        reverseStack(stack2);
        System.out.print("Single Element Stack: ");
        printStack(stack2);  // Expected: [1]
        System.out.println();

        // Test Case 3: Example 1 from the prompt
        Stack<Integer> stack3 = new Stack<>();
        stack3.push(1);
        stack3.push(2);
        stack3.push(3);
        stack3.push(4);
        reverseStack(stack3);
        System.out.print("Stack [1, 2, 3, 4]: ");
        printStack(stack3);  // Expected: [4, 3, 2, 1]
        System.out.println();

        // Test Case 4: Example 2 from the prompt
        Stack<Integer> stack4 = new Stack<>();
        stack4.push(5);
        stack4.push(10);
        stack4.push(15);
        reverseStack(stack4);
        System.out.print("Stack [5, 10, 15]: ");
        printStack(stack4);  // Expected: [15, 10, 5]
        System.out.println();

        // Test Case 5: Stack with negative numbers
        Stack<Integer> stack5 = new Stack<>();
        stack5.push(-1);
        stack5.push(0);
        stack5.push(1);
        reverseStack(stack5);
        System.out.print("Stack [-1, 0, 1]: ");
        printStack(stack5); // Expected: [1, 0, -1]
        System.out.println();

        // Test Case 6: Stack with duplicate numbers
        Stack<Integer> stack6 = new Stack<>();
        stack6.push(2);
        stack6.push(2);
        stack6.push(2);
        reverseStack(stack6);
        System.out.print("Stack [2, 2, 2]: ");
        printStack(stack6); // Expected: [2, 2, 2]
        System.out.println();
    }

    /**
     * Prints the elements of a stack from top to bottom.
     *
     * @param stack The stack to print.
     */
    private static void printStack(Stack<Integer> stack) {
        Stack<Integer> tempStack = new Stack<>(); // Temporary stack to preserve the original stack

        System.out.print("[");
        boolean first = true;
        while (!stack.isEmpty()) {
            int element = stack.pop();
            tempStack.push(element); // Push to temp stack
            if (!first) {
                System.out.print(", ");
            }
            System.out.print(element);
            first = false;
        }
        System.out.print("]");

        // Restore the original stack
        while (!tempStack.isEmpty()) {
            stack.push(tempStack.pop());
        }
    }

    /*
     * Time Complexity Analysis:
     *
     * The `reverseStack` method calls `insertAtBottom` for each element in the stack.
     * The `insertAtBottom` method, in the worst case, removes all elements from the stack before
     * inserting the item at the bottom and then pushing the removed elements back. This takes O(n) time,
     * where n is the number of elements in the stack.
     * Since `reverseStack` calls `insertAtBottom` n times, the overall time complexity is O(n * n) = O(n^2).
     *
     * Space Complexity Analysis:
     *
     * The space complexity is determined by the depth of the recursion stack.
     * The `reverseStack` method calls itself recursively for each element in the stack, leading to a recursion
     * depth of n in the worst case. The `insertAtBottom` method also calls itself recursively, contributing
     * to the recursion depth up to n.
     * Therefore, the space complexity is O(n) due to the recursion stack.  No auxiliary data structures are used beyond the call stack.
     */
}
