import java.util.HashMap;
import java.util.Map;
import java.util.concurrent.ConcurrentHashMap;

public class RateLimiter {

    private final int maxRequests;
    private final int timeWindowSeconds;
    private final Map<String, RequestLog> requestLogs;

    /**
     * Constructor for the RateLimiter.
     *
     * @param maxRequests       The maximum number of requests allowed within the time window.
     * @param timeWindowSeconds The time window in seconds for the rate limit.
     */
    public RateLimiter(int maxRequests, int timeWindowSeconds) {
        this.maxRequests = maxRequests;
        this.timeWindowSeconds = timeWindowSeconds;
        // Use ConcurrentHashMap for thread safety as the limiter is likely to be used in a multithreaded environment
        this.requestLogs = new ConcurrentHashMap<>();
    }

    /**
     * Determines if a request from a given customer is allowed based on the rate limit.
     *
     * @param customerId The ID of the customer making the request.
     * @return true if the request is allowed, false otherwise.
     *
     * Time Complexity: O(1) -  Accessing a HashMap and performing basic arithmetic operations.
     * Space Complexity: O(1) - Constant space for local variables.
     */
    public boolean allowRequest(String customerId) {
        long currentTime = System.currentTimeMillis();

        RequestLog requestLog = requestLogs.computeIfAbsent(customerId, k -> new RequestLog());

        // Clean up the request log by removing requests older than the time window.
        requestLog.removeExpiredRequests(currentTime - (timeWindowSeconds * 1000L));

        // Check if the number of requests within the time window exceeds the maximum allowed.
        if (requestLog.getRequests().size() < maxRequests) {
            // Add the current request to the log.
            requestLog.addRequest(currentTime);
            return true; // Request allowed.
        } else {
            return false; // Request blocked due to rate limit.
        }
    }


    // Helper class to store request logs for each customer.
    private static class RequestLog {
        private final java.util.Queue<Long> requests; // Using a Queue to maintain the order of requests

        public RequestLog() {
            this.requests = new java.util.LinkedList<>();
        }

        public java.util.Queue<Long> getRequests() {
            return requests;
        }

        /**
         * Adds a request timestamp to the request log.
         *
         * @param timestamp The timestamp of the request.
         *
         * Time Complexity: O(1) -  Adding an element to a LinkedList (Queue)
         * Space Complexity: O(1) - Constant space for the operation
         */
        public void addRequest(long timestamp) {
            requests.add(timestamp);
        }

        /**
         * Removes expired requests from the request log.
         *
         * @param expirationThreshold The timestamp before which requests are considered expired.
         *
         * Time Complexity: O(N) in the worst case, where N is the number of requests in the queue.
         *                  However, on average, it's closer to O(K) where K is the number of expired requests.
         * Space Complexity: O(1) - Constant space for the operation.
         */
        public void removeExpiredRequests(long expirationThreshold) {
            while (!requests.isEmpty() && requests.peek() < expirationThreshold) {
                requests.poll();
            }
        }
    }

    public static void main(String[] args) throws InterruptedException {
        // Example Usage and Test Cases
        RateLimiter rateLimiter = new RateLimiter(2, 1); // 2 requests per 1 second

        // Test Case 1: Basic Rate Limiting
        System.out.println("Test Case 1: Basic Rate Limiting");
        System.out.println("Request 1 allowed: " + rateLimiter.allowRequest("user1")); // true
        System.out.println("Request 2 allowed: " + rateLimiter.allowRequest("user1")); // true
        System.out.println("Request 3 allowed: " + rateLimiter.allowRequest("user1")); // false (rate limited)
        Thread.sleep(1000); // Wait for 1 second (time window)
        System.out.println("Request 4 allowed: " + rateLimiter.allowRequest("user1")); // true (time window reset)
        System.out.println();

        // Test Case 2: Multiple Users
        System.out.println("Test Case 2: Multiple Users");
        System.out.println("User2 Request 1 allowed: " + rateLimiter.allowRequest("user2")); // true
        System.out.println("User2 Request 2 allowed: " + rateLimiter.allowRequest("user2")); // true
        System.out.println("User2 Request 3 allowed: " + rateLimiter.allowRequest("user2")); // false
        System.out.println("User1 Request 1 allowed: " + rateLimiter.allowRequest("user1")); // true
        System.out.println();

        // Test Case 3: Edge Case - Time Window Reset Exactly
        System.out.println("Test Case 3: Edge Case - Time Window Reset Exactly");
        System.out.println("Request 1 allowed: " + rateLimiter.allowRequest("user3")); // true
        System.out.println("Request 2 allowed: " + rateLimiter.allowRequest("user3")); // true
        Thread.sleep(1000);
        System.out.println("Request 3 allowed: " + rateLimiter.allowRequest("user3")); // true (time window reset)
        System.out.println();

        // Test Case 4: Different rate limits for each customer (using separate rate limiters)
        System.out.println("Test Case 4: Different rate limits per user (using separate RateLimiters)");
        RateLimiter rateLimiterUser4 = new RateLimiter(5, 2); // 5 req / 2 sec
        System.out.println("User4 Request 1 allowed: " + rateLimiterUser4.allowRequest("user4")); // true
        System.out.println("User4 Request 2 allowed: " + rateLimiterUser4.allowRequest("user4")); // true
        System.out.println("User4 Request 3 allowed: " + rateLimiterUser4.allowRequest("user4")); // true
        System.out.println("User4 Request 4 allowed: " + rateLimiterUser4.allowRequest("user4")); // true
        System.out.println("User4 Request 5 allowed: " + rateLimiterUser4.allowRequest("user4")); // true
        System.out.println("User4 Request 6 allowed: " + rateLimiterUser4.allowRequest("user4")); // false
        System.out.println();

        // Test Case 5: Fast requests exceeding limit immediately
        System.out.println("Test Case 5: Fast requests exceeding limit immediately");
        System.out.println("User5 Request 1 allowed: " + rateLimiter.allowRequest("user5")); // true
        System.out.println("User5 Request 2 allowed: " + rateLimiter.allowRequest("user5")); // true
        System.out.println("User5 Request 3 allowed: " + rateLimiter.allowRequest("user5")); // false
        System.out.println("User5 Request 4 allowed: " + rateLimiter.allowRequest("user5")); // false
    }
}
