import java.util.Arrays;

public class StringSearch {

    /**
     * Determines if each small string in the input array is contained within the big string.
     * This method avoids using built-in String.contains() or similar functions.
     *
     * @param bigString   The string to search within.
     * @param smallStrings An array of strings to search for within the big string.
     * @return An array of booleans, where each boolean indicates whether the small string
     *         at that index is found in the big string.
     */
    public boolean[] stringSearch(String bigString, String[] smallStrings) {
        if (bigString == null || smallStrings == null) {
            return new boolean[0]; // Handle null input
        }

        int numSmallStrings = smallStrings.length;
        boolean[] results = new boolean[numSmallStrings];

        for (int i = 0; i < numSmallStrings; i++) {
            results[i] = contains(bigString, smallStrings[i]);
        }

        return results;
    }

    /**
     * Helper method to check if a small string is contained within a big string
     * without using built-in contains() function.  It employs a naive string matching
     * algorithm.
     *
     * @param bigString   The string to search within.
     * @param smallString The string to search for.
     * @return True if the small string is found in the big string, false otherwise.
     */
    private boolean contains(String bigString, String smallString) {
        if (smallString == null || smallString.isEmpty()) {
            return true; // Empty string is considered to be present
        }
        if (bigString == null || bigString.isEmpty()) {
            return false; // Small string cannot be present if big string is null or empty.
        }

        int bigStringLength = bigString.length();
        int smallStringLength = smallString.length();

        if (smallStringLength > bigStringLength) {
            return false; // Small string longer than big string, impossible to be present.
        }

        for (int i = 0; i <= bigStringLength - smallStringLength; i++) {
            boolean match = true;
            for (int j = 0; j < smallStringLength; j++) {
                if (bigString.charAt(i + j) != smallString.charAt(j)) {
                    match = false;
                    break;
                }
            }
            if (match) {
                return true;
            }
        }

        return false;
    }

    public static void main(String[] args) {
        StringSearch stringSearch = new StringSearch();

        // Test case 1
        String bigString1 = "this is a big string";
        String[] smallStrings1 = {"this", "yo", "is", "a", "bigger", "string", "kappa"};
        boolean[] result1 = stringSearch.stringSearch(bigString1, smallStrings1);
        System.out.println("Test Case 1: Big String = \"" + bigString1 + "\", Small Strings = " + Arrays.toString(smallStrings1) + ", Result = " + Arrays.toString(result1));

        // Test case 2
        String bigString2 = "Mary goes to the shopping center every week.";
        String[] smallStrings2 = {"to", "Mary", "centers", "shop", "shopping", "string", "kappa"};
        boolean[] result2 = stringSearch.stringSearch(bigString2, smallStrings2);
        System.out.println("Test Case 2: Big String = \"" + bigString2 + "\", Small Strings = " + Arrays.toString(smallStrings2) + ", Result = " + Arrays.toString(result2));

        // Test case 3: Empty big string
        String bigString3 = "";
        String[] smallStrings3 = {"a", "b"};
        boolean[] result3 = stringSearch.stringSearch(bigString3, smallStrings3);
        System.out.println("Test Case 3: Big String = \"\", Small Strings = " + Arrays.toString(smallStrings3) + ", Result = " + Arrays.toString(result3));

        // Test case 4: Empty small strings array
        String bigString4 = "some string";
        String[] smallStrings4 = {};
        boolean[] result4 = stringSearch.stringSearch(bigString4, smallStrings4);
        System.out.println("Test Case 4: Big String = \"some string\", Small Strings = {}, Result = " + Arrays.toString(result4));

        // Test case 5: Small string longer than big string
        String bigString5 = "abc";
        String[] smallStrings5 = {"abcd", "abcde"};
        boolean[] result5 = stringSearch.stringSearch(bigString5, smallStrings5);
        System.out.println("Test Case 5: Big String = \"abc\", Small Strings = " + Arrays.toString(smallStrings5) + ", Result = " + Arrays.toString(result5));

        // Test case 6: Overlapping small string
        String bigString6 = "abababab";
        String[] smallStrings6 = {"aba", "bab"};
        boolean[] result6 = stringSearch.stringSearch(bigString6, smallStrings6);
        System.out.println("Test Case 6: Big String = \"abababab\", Small Strings = " + Arrays.toString(smallStrings6) + ", Result = " + Arrays.toString(result6));

        // Test case 7: Null big string
        String bigString7 = null;
        String[] smallStrings7 = {"aba"};
        boolean[] result7 = stringSearch.stringSearch(bigString7, smallStrings7);
        System.out.println("Test Case 7: Big String = null, Small Strings = " + Arrays.toString(smallStrings7) + ", Result = " + Arrays.toString(result7));

         // Test case 8: Null small strings array
        String bigString8 = "hello";
        String[] smallStrings8 = null;
        boolean[] result8 = stringSearch.stringSearch(bigString8, smallStrings8);
        System.out.println("Test Case 8: Big String = \"hello\", Small Strings = null, Result = " + Arrays.toString(result8));

        //Test case 9: Small string is empty string
        String bigString9 = "hello";
        String[] smallStrings9 = {""};
        boolean[] result9 = stringSearch.stringSearch(bigString9, smallStrings9);
        System.out.println("Test Case 9: Big String = \"hello\", Small Strings = {\"\"}, Result = " + Arrays.toString(result9));
    }


    /**
     * Time Complexity Analysis:
     * - stringSearch: O(n * m * k), where n is the number of small strings, m is the length of the big string,
     *                and k is the average length of the small strings.  The outer loop iterates n times,
     *                and the contains method has a time complexity of O(m * k).
     * - contains: O(m * k), where m is the length of the big string and k is the length of the small string.
     *             In the worst case, the outer loop in 'contains' iterates m-k+1 times, and the inner loop iterates k times.
     *
     * Space Complexity Analysis:
     * - stringSearch: O(n), where n is the number of small strings.  This is due to the 'results' boolean array.
     * - contains: O(1), uses constant extra space.
     *
     * Overall:
     * Time Complexity: O(n * m * k)
     * Space Complexity: O(n)
     */
}
