import java.util.Arrays;

public class LongestIncreasingSubsequence {

    /**
     * Finds the length of the longest increasing subsequence (LIS) in an array of integers.
     *
     * @param nums The input array of integers.
     * @return The length of the LIS.
     *
     * Time Complexity: O(n log n), where n is the length of the input array.  Binary search is performed for each element.
     * Space Complexity: O(n), where n is the length of the input array. The 'tails' array can grow up to the size of the input array in the worst case.
     */
    public int lengthOfLIS(int[] nums) {
        if (nums == null || nums.length == 0) {
            return 0;
        }

        // 'tails[i]' is the smallest tail of all increasing subsequences with length 'i+1'.
        int[] tails = new int[nums.length];
        int size = 0; // Length of the longest increasing subsequence found so far.

        for (int num : nums) {
            // Binary search to find the smallest tail that is greater than or equal to the current number.
            int i = 0, j = size;
            while (i != j) {
                int m = (i + j) / 2;
                if (tails[m] < num) {
                    i = m + 1;
                } else {
                    j = m;
                }
            }

            // If we found a tail greater than or equal to the current number, we replace it.
            // Otherwise, it means the current number extends the longest increasing subsequence by 1.
            tails[i] = num;
            if (i == size) {
                size++;
            }
        }

        return size;
    }

