import java.util.ArrayList;
import java.util.List;

public class ArrayReduction {

    /**
     * Finds the last remaining element in the array after performing the reducing operation.
     *
     * @param arr The input array.
     * @param n   The integer N for modulo operation.
     * @return The last remaining element.
     */
    public int findLastRemaining(int[] arr, int n) {
        // Convert the input array to a List for easier manipulation.
        List<Integer> list = new ArrayList<>();
        for (int num : arr) {
            list.add(num);
        }

        // Reduce the array until only one element remains.
        while (list.size() > 1) {
            // Get the first and last elements.
            int first = list.remove(0);
            int last = list.remove(list.size() - 1);

            // Calculate the sum and modulo.
            int sumMod = (first + last) % n;

            // Insert the new element at the middle position.
            int middleIndex = (list.size() / 2); // K/2 for array size K
            list.add(middleIndex, sumMod);
        }

        // Return the last remaining element.
        return list.get(0);
    }

    /**
     * Main method for testing the solution.
     *
     * @param args Command line arguments (not used).
     */
    public static void main(String[] args) {
        ArrayReduction arrayReduction = new ArrayReduction();

        // Test cases
