        int[] arr4 = {1};
        int n4 = 5;
        System.out.println("Input: arr = {1}, N = 5");
        System.out.println("Output: " + arrayReduction.findLastRemaining(arr4, n4)); // Expected: 1
        System.out.println();

        int[] arr5 = {1, 2};
        int n5 = 5;
        System.out.println("Input: arr = {1, 2}, N = 5");
